import { pgTable, text, serial, integer, boolean, timestamp, unique, uniqueIndex, varchar, index } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  credits: integer("credits").notNull().default(1000),
  suns: integer("suns").notNull().default(100),
  dna: integer("dna").notNull().default(0),
  hearts: integer("hearts").notNull().default(0),
  lastPassiveIncomeAt: timestamp("last_passive_income_at"),
  // ‚úÖ REMOVED: lastActiveAt - column doesn't exist in database
  tickets: integer("tickets").notNull().default(0),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  updatedAt: timestamp("updated_at").notNull().defaultNow(),
});

export const userUnlockedFeatures = pgTable("user_unlocked_features", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  featureName: text("feature_name").notNull(), // 'dna', 'marie-slot', 'schlossgarten'
  unlockedAt: timestamp("unlocked_at").notNull().defaultNow(),
  creditsSpent: integer("credits_spent").notNull(),
}, (table) => ({
  uniqueUserFeature: unique().on(table.userId, table.featureName)
}));

export const seeds = pgTable("seeds", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  rarity: text("rarity").notNull(), // common, uncommon, rare, legendary
  price: integer("price").notNull(),
  description: text("description"),
  imageUrl: text("image_url"),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const userSeeds = pgTable("user_seeds", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
