export type CreateBouquetRequest = z.infer<typeof createBouquetSchema>;
export type PlaceBouquetRequest = z.infer<typeof placeBouquetSchema>;
export type UnlockedField = typeof unlockedFields.$inferSelect;
export type UnlockFieldRequest = z.infer<typeof unlockFieldSchema>;
export type WeeklyChallenge = typeof weeklyChallenges.$inferSelect;
export type WeeklyChallengeProgress = typeof weeklyChallengeProgress.$inferSelect;
export type ChallengeDonation = typeof challengeDonations.$inferSelect;
export type ChallengeReward = typeof challengeRewards.$inferSelect;
export type UserNotification = typeof userNotifications.$inferSelect;
export type DonateChallengeFlowerRequest = z.infer<typeof donateChallengeFlowerSchema>;
export type PlaceButterflyOnFieldRequest = z.infer<typeof placeButterflyOnFieldSchema>;
export type PlaceFlowerOnFieldRequest = z.infer<typeof placeFlowerOnFieldSchema>;
export type PondFeedingProgress = typeof pondFeedingProgress.$inferSelect;
export type FieldFish = typeof fieldFish.$inferSelect;

// Marie Posa trading tracker
export const mariePosaTracker = pgTable("marie_posa_tracker", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  lastTradeAt: timestamp("last_trade_at").notNull().defaultNow(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
}, (table) => ({
  uniqueUserMariePosa: uniqueIndex("unique_user_marie_posa").on(table.userId),
}));

// Daily Items for Ticket Redemption
export const dailyItems = pgTable("daily_items", {
  id: serial("id").primaryKey(),
  date: varchar("date", { length: 10 }).notNull(), // YYYY-MM-DD format

  // Daily rare items with rarity 2+ for ticket redemption
  flowerId: integer("flower_id").notNull(),
  flowerRarity: integer("flower_rarity").notNull(),
  butterflyId: integer("butterfly_id").notNull(),
  butterflyRarity: integer("butterfly_rarity").notNull(),
  caterpillarId: integer("caterpillar_id").notNull(),
  caterpillarRarity: integer("caterpillar_rarity").notNull(),
  fishId: integer("fish_id").notNull(),
  fishRarity: integer("fish_rarity").notNull(),

  createdAt: timestamp("created_at").defaultNow(),
}, (table) => ({
  dateUnique: unique().on(table.date),
}));

// Track daily prize redemptions per user
export const dailyRedemptions = pgTable("daily_redemptions", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  date: varchar("date", { length: 10 }).notNull(), // YYYY-MM-DD format
  prizeType: varchar("prize_type", { length: 50 }).notNull(), // daily-flower, daily-butterfly, etc.
  redeemedAt: timestamp("redeemed_at").defaultNow(),
}, (table) => ({
  userDatePrizeUnique: unique().on(table.userId, table.date, table.prizeType),
}));

// User Notifications - for login notifications about rewards
export const userNotifications = pgTable("user_notifications", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  type: varchar("type", { length: 50 }).notNull(), // 'challenge_reward', 'achievement', 'system'
  title: text("title").notNull(), // "üèÜ Challenge Gewonnen!"
  message: text("message").notNull(), // Detailed message about the reward
  isRead: boolean("is_read").notNull().default(false),
  // Optional reward details
  rewardType: varchar("reward_type", { length: 50 }), // 'butterfly', 'credits', 'vip_butterfly'
  rewardItemId: integer("reward_item_id"), // Butterfly ID, item ID etc
  rewardItemName: text("reward_item_name"), // Butterfly name, item name
  rewardItemRarity: text("reward_item_rarity"), // 'legendary', 'mythical'
  rewardAmount: integer("reward_amount"), // Credits amount or quantity
  // Challenge specific info
  challengeId: integer("challenge_id").references(() => weeklyChallenges.id),
  challengeRank: integer("challenge_rank"), // 1, 2, 3...

  createdAt: timestamp("created_at").notNull().defaultNow(),
  readAt: timestamp("read_at"), // When user acknowledged the notification
}, (table) => ({
  userNotReadIdx: index("idx_user_notif_unread").on(table.userId, table.isRead),
}));

// Castle Garden Tables
export const castleUnlockedParts = pgTable("castle_unlocked_parts", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  partName: text("part_name").notNull(), // z.B. "SteinBauteil"
  price: integer("price").notNull(), // Preis in Credits
  unlockedAt: timestamp("unlocked_at").notNull().defaultNow(),
}, (table) => ({
  userPartUnique: unique().on(table.userId, table.partName),
}));

export const castleGridState = pgTable("castle_grid_state", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  gridX: integer("grid_x").notNull(), // 0-24 (25 Spalten)
  gridY: integer("grid_y").notNull(), // 0-14 (15 Reihen)
  partName: text("part_name").notNull(), // Welches Bauteil liegt hier
  placedAt: timestamp("placed_at").notNull().defaultNow(),
}, (table) => ({
  userPositionUnique: unique().on(table.userId, table.gridX, table.gridY),
}));

// Aquarium types
export type AquariumTank = typeof aquariumTanks.$inferSelect;
export type NewAquariumTank = typeof aquariumTanks.$inferInsert;
export type AquariumFish = typeof aquariumFish.$inferSelect;
export type NewAquariumFish = typeof aquariumFish.$inferInsert;

// Marie Posa types  
export type MariePosaTracker = typeof mariePosaTracker.$inferSelect;
export type NewMariePosaTracker = typeof mariePosaTracker.$inferInsert;

// Daily Items types
export type DailyItems = typeof dailyItems.$inferSelect;
export type NewDailyItems = typeof dailyItems.$inferInsert;

// Daily Redemptions types
export type DailyRedemption = typeof dailyRedemptions.$inferSelect;
export type NewDailyRedemption = typeof dailyRedemptions.$inferInsert;

// Collection Statistics for Encyclopedia - tracks lifetime item acquisition
export const collectionStats = pgTable("collection_stats", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  itemType: varchar("item_type", { length: 20 }).notNull(), // 'flowers', 'butterflies', 'caterpillars', 'fish'
  itemId: integer("item_id").notNull(), // Game item ID (e.g., flower 1-200, butterfly 1-1000)
  totalObtained: integer("total_obtained").notNull().default(1), // Lifetime count
  firstObtainedAt: timestamp("first_obtained_at").notNull().defaultNow(),
  lastObtainedAt: timestamp("last_obtained_at").notNull().defaultNow(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  updatedAt: timestamp("updated_at").notNull().defaultNow(),
}, (table) => ({
  // Unique constraint: one stats entry per user-itemType-itemId combination
  uniqueUserItemStats: unique().on(table.userId, table.itemType, table.itemId)
}));

// Castle Garden types
export type CastleUnlockedPart = typeof castleUnlockedParts.$inferSelect;
export type NewCastleUnlockedPart = typeof castleUnlockedParts.$inferInsert;
export type CastleGridState = typeof castleGridState.$inferSelect;
export type NewCastleGridState = typeof castleGridState.$inferInsert;

// Collection Stats types
export type CollectionStats = typeof collectionStats.$inferSelect;
export type NewCollectionStats = typeof collectionStats.$inferInsert;

