@tailwind base;
@tailwind components;
@tailwind utilities;

/* Custom animations for slot machine */
@keyframes spin-slow {
  from {
    transform: translateY(0);
  }
  to {
    transform: translateY(-100px);
  }
}

@keyframes spin-fast {
  from {
    transform: translateY(0);
  }
  to {
    transform: translateY(-200px);
  }
}

@keyframes recoil {
  0% { transform: translateY(0px); }
  30% { transform: translateY(-8px); }
  70% { transform: translateY(4px); }
  100% { transform: translateY(0px); }
}

/* Golden glow animation for vase frames */
@keyframes gold-glow-random {
  0% { 
    border-color: rgb(100, 116, 139); /* slate-500 */
    box-shadow: 0 0 0px rgba(251, 191, 36, 0); /* transparent gold */
  }
  25% { 
    border-color: rgb(217, 119, 6); /* amber-600 */
