      return;
    }

    // Check if this is a self-purchase (user buying their own listing)
    const listing = marketListings.find(l => l.id === listingId);
    const isSelfPurchase = listing && (listing as any).sellerId === user.id;

    setIsLoading(true);
    try {
      const response = await fetch('/api/market/buy', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'X-User-Id': user?.id.toString() || '1'
        },
        body: JSON.stringify({ listingId, quantity })
      });

      if (response.ok) {
        // Only deduct credits if it's NOT a self-purchase
        if (!isSelfPurchase) {
          await updateCredits(user.id, -totalCost);
        }
        await fetchMarketListings();
        await fetchMySeeds();
        await fetchMyCaterpillars();
        await fetchMyFlowers();
        await fetchMyButterflies();
        await fetchMyFish();
        showNotification('Kauf erfolgreich!', 'success');
      } else {
        const error = await response.json();
        showNotification(error.message || 'Kauf fehlgeschlagen', 'error');
      }
