import { create } from "zustand";

interface AudioState {
  backgroundMusic: HTMLAudioElement | null;
  hitSound: HTMLAudioElement | null;
  successSound: HTMLAudioElement | null;
  isMuted: boolean;
  spinningSound: HTMLAudioElement | null; // F√ºr kontinuierlichen Spin-Sound
  spinStopTimer: NodeJS.Timeout | null; // Timer f√ºr verz√∂gerten Stop

  // Setter functions
  setBackgroundMusic: (music: HTMLAudioElement) => void;
  setHitSound: (sound: HTMLAudioElement) => void;
  setSuccessSound: (sound: HTMLAudioElement) => void;

  // Control functions
  toggleMute: () => void;
  playHit: () => void;
  playSuccess: () => void;
  playSlotSpin: () => void;
  playSlotStop: () => void;
}

export const useAudio = create<AudioState>((set, get) => ({
  backgroundMusic: null,
  hitSound: null,
  successSound: null,
  spinningSound: null,
  spinStopTimer: null,
  isMuted: true, // Start muted by default

  setBackgroundMusic: (music) => set({ backgroundMusic: music }),
  setHitSound: (sound) => set({ hitSound: sound }),
  setSuccessSound: (sound) => set({ successSound: sound }),

  toggleMute: () => {
    const { isMuted } = get();
    const newMutedState = !isMuted;

    // Just update the muted state
    set({ isMuted: newMutedState });

    // Log the change
    console.log(`Sound ${newMutedState ? 'muted' : 'unmuted'}`);
  },

  playHit: () => {
    const { hitSound, isMuted } = get();
    if (hitSound) {
      // If sound is muted, don't play anything
      if (isMuted) {
        console.log("Hit sound skipped (muted)");
        return;
      }

      // Clone the sound to allow overlapping playback
      const soundClone = hitSound.cloneNode() as HTMLAudioElement;
      soundClone.volume = 0.3;
      soundClone.play().catch(error => {
        console.log("Hit sound play prevented:", error);
      });
    }
  },

  playSuccess: () => {
    const { successSound, isMuted } = get();
    if (successSound && !isMuted) {
      successSound.volume = 0.7; // Lautst√§rke einstellen
      successSound.currentTime = 0;
      successSound.play().catch(error => {
        console.log("Success sound play prevented:", error);
      });
    }
  },

  playSlotSpin: () => {
    const { backgroundMusic, isMuted, spinStopTimer } = get();

    // L√∂sche vorherigen Timer falls vorhanden
    if (spinStopTimer) {
      clearTimeout(spinStopTimer);
      set({ spinStopTimer: null });
    }

    if (backgroundMusic && !isMuted) {
      // Erstelle einen kontinuierlichen Spin-Sound aus der Hintergrundmusik
      const spinSound = backgroundMusic.cloneNode() as HTMLAudioElement;
      spinSound.volume = 0.15; // Leiser f√ºr Hintergrund-Effekt
      spinSound.playbackRate = 2.0; // Doppelte Geschwindigkeit f√ºr Maschinen-Effekt
      spinSound.loop = true; // Kontinuierlich wiederholen
      spinSound.play().catch(error => {
        console.log("Slot spin sound play prevented:", error);
      });

      // Speichere Referenz zum Stoppen
      set({ spinningSound: spinSound });
    }
  },

  playSlotStop: () => {
    const { hitSound, spinningSound, isMuted, spinStopTimer } = get();

    // Spiele den Stop-Sound sofort
    if (hitSound && !isMuted) {
      // Doppelter "Stopp" Sound f√ºr h√∂rbareren Effekt
      const soundClone1 = hitSound.cloneNode() as HTMLAudioElement;
      soundClone1.volume = 0.6; // Lauter
      soundClone1.playbackRate = 0.8; // Langsamer, tiefer
      soundClone1.play().catch(error => {
        console.log("Slot stop sound play prevented:", error);
      });

      // Zweiter Sound nach kurzer Verz√∂gerung
      setTimeout(() => {
        const soundClone2 = hitSound.cloneNode() as HTMLAudioElement;
        soundClone2.volume = 0.4;
        soundClone2.playbackRate = 1.2; // H√∂her f√ºr "Klick" Effekt
        soundClone2.play().catch(error => {
          console.log("Slot stop sound 2 play prevented:", error);
        });
      }, 100);
    }

    // Stoppe den kontinuierlichen Spin-Sound erst nach 2 Sekunden, aber nur einmal
    if (spinningSound && !spinStopTimer) {
      const timer = setTimeout(() => {
        const { spinningSound: currentSpinningSound } = get();
        if (currentSpinningSound) {
          currentSpinningSound.pause();
          currentSpinningSound.currentTime = 0;
          set({ spinningSound: null, spinStopTimer: null });
        }
      }, 2000); // 2 Sekunden Verz√∂gerung

      set({ spinStopTimer: timer });
    }
  }
}));

client/src/lib/stores/useAuth.tsx.content:
import { create } from "zustand";
import { subscribeWithSelector, persist } from "zustand/middleware";

export interface User {
  id: number;
  username: string;
  credits: number;
}

export interface UserNotification {
  id: number;
  userId: number;
  type: string;
  title: string;
  message: string;
  isRead: boolean;
  rewardType?: string;
  rewardItemId?: number;
  rewardItemName?: string;
  rewardItemRarity?: string;
  rewardAmount?: number;
  challengeId?: number;
  challengeRank?: number;
  createdAt: string;
  readAt?: string;
}

interface AuthState {
  user: User | null;
  isLoading: boolean;
  error: string | null;
  isAuthenticated: boolean;
  hasCheckedAuth: boolean; // üîí SECURITY: Track if server auth has been verified

  // Notifications
  notifications: UserNotification[];
  showNotifications: boolean;
  hasUnreadNotifications: boolean;

  // Actions
  login: (username: string, password: string) => Promise<void>;
  register: (username: string, password: string) => Promise<void>;
  logout: () => Promise<void>;
  checkAuth: () => Promise<void>;
  clearError: () => void;

  // Notification actions  
  loadNotifications: () => Promise<void>;
  setShowNotifications: (show: boolean) => void;
  markNotificationAsRead: (notificationId: number) => Promise<void>;
  markAllNotificationsAsRead: () => Promise<void>;
}

export const useAuth = create<AuthState>()(
  subscribeWithSelector(
    persist(
      (set, get) => ({
        user: null,
        isLoading: false,
        error: null,
        isAuthenticated: false,
        hasCheckedAuth: false, // üîí SECURITY: Starts false until server verification

        // Notifications
        notifications: [],
        showNotifications: false,
        hasUnreadNotifications: false,

        login: async (username: string, password: string) => {
          set({ isLoading: true, error: null });

          try {
            const response = await fetch("/api/auth/login", {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify({ username, password }),
              credentials: "include", // Include cookies for JWT
            });

            if (!response.ok) {
              const errorData = await response.json();
              throw new Error(errorData.message || "Login failed");
            }

            const data = await response.json();
            set({ user: data.user, isLoading: false, isAuthenticated: true, hasCheckedAuth: true });
            console.log('üîê Login successful:', data.user.username);

            // Load notifications after successful login
            get().loadNotifications();
          } catch (error) {
            set({ 
              error: error instanceof Error ? error.message : "Login failed", 
              isLoading: false,
              isAuthenticated: false
            });
            console.error('üîê Login failed:', error);
          }
        },

        register: async (username: string, password: string) => {
          set({ isLoading: true, error: null });

          try {
            const response = await fetch("/api/auth/register", {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify({ username, password }),
              credentials: "include", // Include cookies for JWT
            });

            if (!response.ok) {
              const errorData = await response.json();
              throw new Error(errorData.message || "Registration failed");
            }

            const data = await response.json();
            set({ user: data.user, isLoading: false, isAuthenticated: true, hasCheckedAuth: true });
            console.log('üîê Registration successful:', data.user.username);
          } catch (error) {
            set({ 
              error: error instanceof Error ? error.message : "Registration failed", 
              isLoading: false,
              isAuthenticated: false
            });
            console.error('üîê Registration failed:', error);
          }
        },

        logout: async () => {
          try {
            await fetch("/api/auth/logout", {
              method: "POST",
              credentials: "include", // Include cookies for JWT
            });
            console.log('üîê Logout successful');
          } catch (error) {
            console.error('üîê Logout error:', error);
          } finally {
            set({ user: null, error: null, isAuthenticated: false, hasCheckedAuth: true });
          }
        },

        checkAuth: async () => {
          try {
            set({ isLoading: true });
            const response = await fetch("/api/auth/me", {
              method: "GET",
              credentials: "include", // Include cookies for JWT
            });

            if (response.ok) {
              const data = await response.json();
              set({ 
                user: { 
                  id: data.user.id, 
                  username: data.user.username, 
                  credits: 0 // Will be fetched separately
                }, 
                isAuthenticated: true,
                isLoading: false,
                hasCheckedAuth: true
              });
              console.log('üîê Authentication verified:', data.user.username);
            } else {
              set({ user: null, isAuthenticated: false, isLoading: false, hasCheckedAuth: true });
            }
          } catch (error) {
            console.error('üîê Auth check failed:', error);
            set({ user: null, isAuthenticated: false, isLoading: false, hasCheckedAuth: true });
          }
        },

        clearError: () => {
          set({ error: null });
        },

        // Notification actions
        loadNotifications: async () => {
          const state = get();
          if (!state.user) return;

          try {
            const response = await fetch(`/api/user/${state.user.id}/notifications`);
            if (response.ok) {
              const data = await response.json();
              const notifications = data.notifications || [];
              const hasUnread = notifications.some((n: UserNotification) => !n.isRead);

              set({ 
                notifications,
                hasUnreadNotifications: hasUnread,
                showNotifications: hasUnread // Auto-show if there are unread notifications
              });

              console.log(`üì® Loaded ${notifications.length} notifications (${hasUnread ? 'has unread' : 'all read'})`);
            }
          } catch (error) {
            console.error('Failed to load notifications:', error);
          }
        },

        setShowNotifications: (show: boolean) => {
          set({ showNotifications: show });
        },

        markNotificationAsRead: async (notificationId: number) => {
          const state = get();
          if (!state.user) return;

          try {
            const response = await fetch(`/api/user/${state.user.id}/notifications/${notificationId}/read`, {
              method: 'POST'
            });
            if (response.ok) {
              set(state => ({
                notifications: state.notifications.map(n => 
                  n.id === notificationId ? { ...n, isRead: true } : n
                ),
                hasUnreadNotifications: state.notifications.some(n => 
                  n.id !== notificationId && !n.isRead
                )
              }));
            }
          } catch (error) {
            console.error('Failed to mark notification as read:', error);
          }
        },

        markAllNotificationsAsRead: async () => {
          const state = get();
          if (!state.user) return;

          try {
            const response = await fetch(`/api/user/${state.user.id}/notifications/read-all`, {
              method: 'POST'
            });
            if (response.ok) {
              set(state => ({
                notifications: state.notifications.map(n => ({ ...n, isRead: true })),
                hasUnreadNotifications: false
              }));
            }
          } catch (error) {
            console.error('Failed to mark all notifications as read:', error);
          }
        },
      }),
      {
        name: 'mariposa-auth', // localStorage key
        partialize: (state) => ({ user: state.user, isAuthenticated: state.isAuthenticated }), // üîí SECURITY: Don't persist hasCheckedAuth - force re-check on reload
      }
    )
  )
);

