                    <div className="absolute inset-0 h-16 w-16 mx-auto text-purple-400 animate-ping opacity-20"></div>
                  </div>
                  <p className="text-slate-300 text-xl mb-3">üíê Keine Bouquets verf√ºgbar</p>
                  <p className="text-slate-400 text-lg">Erstelle zuerst Bouquets im Bouquets-Tab</p>
                </div>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-3 max-h-60 overflow-y-auto">
                {userBouquets.filter(b => b.quantity > 0).map((bouquet) => (
                  <Card 
                    key={bouquet.id} 
                    className={`cursor-pointer transition-all duration-300 hover:scale-105 shadow-lg ${
                      selectedBouquet?.id === bouquet.id 
                        ? 'bg-gradient-to-br from-purple-800/60 to-pink-800/60 border-2 border-purple-400 ring-2 ring-purple-400 shadow-purple-500/50' 
                        : 'bg-gradient-to-br from-slate-800 to-slate-900 border-2 border-slate-600 hover:border-purple-400/50'
                    }`}
                    onClick={() => setSelectedBouquet(bouquet)}
                  >
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-3">
                        <RarityImage 
                          src="/Blumen/Bouquet.jpg"
                          alt="Bouquet"
                          rarity={(bouquet.bouquetRarity || "common") as RarityTier}
                          size="medium"
                          className="w-12 h-12"
                        />
                        <div className="flex-1">
                          <h4 className="font-bold text-white text-sm">{bouquet.bouquetName || `Bouquet #${bouquet.id}`}</h4>
                          <div className="flex items-center justify-between gap-2">
                            <Badge className="text-xs text-rose-400 bg-rose-400/20">
                              Bouquet
                            </Badge>
                            <span className="text-sm font-bold text-green-400">x{bouquet.quantity}</span>
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>

          {/* Enhanced Action Buttons */}
          <div className="flex items-center justify-between pt-4">
            <Button
              variant="outline"
              onClick={onClose}
              className="text-slate-300 border-2 border-slate-600 hover:bg-slate-700 hover:border-slate-500 text-lg px-6 py-3 transition-all duration-300"
            >
              ‚ùå Abbrechen
            </Button>
            <Button
              onClick={handlePlaceBouquet}
              disabled={!selectedBouquet}
              className="bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 disabled:opacity-50 disabled:cursor-not-allowed text-lg px-6 py-3 font-bold transition-all duration-300 hover:scale-110 shadow-lg"
            >
              <div className="relative">
                <Clock className="h-5 w-5 mr-2 animate-pulse" />
              </div>
              üíê Bouquet platzieren
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

server/butterflySpawner.ts.content:
import { postgresStorage as storage } from './postgresStorage';
import type { RarityTier } from '@shared/rarity';

/**
 * Individual Butterfly Spawning System
 * - Checks bouquets every 60 seconds for individual spawn times
 * - Each bouquet has its own nextSpawnAt timestamp
 * - Spawns butterflies based on bouquet rarity and individual timing
 * - Automatically manages expired bouquets
 */
export class ButterflySpawner {
  private intervalId: NodeJS.Timeout | null = null;
  private isRunning = false;

  start() {
    if (this.isRunning) {
      console.log('ü¶ã Butterfly spawner already running');
      return;
    }

    this.isRunning = true;
    console.log('ü¶ã Starting butterfly spawning system...');

    // Run immediately once, then start interval
    this.checkForButterflySpawns();

    // Individual timing checks every 60 seconds
    const CHECK_INTERVAL = 60 * 1000; // 1 minute

    const scheduleNext = () => {
      if (!this.isRunning) return;

      console.log(`ü¶ã Next butterfly check in 1:00 minute`);

      this.intervalId = setTimeout(() => {
        this.checkForButterflySpawns();
        scheduleNext(); // Schedule the next check
      }, CHECK_INTERVAL);
    };

    scheduleNext();
  }

  stop() {
    if (this.intervalId) {
      clearTimeout(this.intervalId);
      this.intervalId = null;
    }
    this.isRunning = false;
    console.log('ü¶ã Butterfly spawner stopped');
  }

  private async checkForButterflySpawns() {
    try {
      console.log('ü¶ã Checking individual bouquet spawn times...');

      const currentTime = new Date();
      let totalSpawns = 0;
      let totalChecked = 0;

      // Get all users with active bouquets  
      const allUsers = await storage.getAllUsersWithStatus();

      for (const user of allUsers) {
        try {
          const placedBouquets = await storage.getPlacedBouquets(user.id);
          const activeBouquets = placedBouquets.filter(pb => new Date(pb.expiresAt) > currentTime);

          if (activeBouquets.length === 0) {
            continue; // Skip this user, no active bouquets
          }

          for (const placedBouquet of activeBouquets) {
            totalChecked++;

            // Check if this bouquet is ready to spawn (individual timing)
            const nextSpawnTime = new Date((placedBouquet as any).nextSpawnAt);
            if (currentTime < nextSpawnTime) {
              continue; // Not time yet for this bouquet
            }

            // Check how many butterflies already spawned for this bouquet
            const existingButterflies = await storage.getFieldButterflies(user.id);
            const butterflyCount = existingButterflies.filter(fb => fb.bouquetId === placedBouquet.bouquetId).length;

            // 4-Slot System: Each bouquet has exactly 4 spawn opportunities (one per slot)
            const currentSlot = (placedBouquet as any).currentSpawnSlot || 1;

            if (currentSlot > 4) {
              continue; // This bouquet has completed all 4 spawn slots
            }

            // Use the rarity stored in the placed bouquet
            const rarity = (placedBouquet as any).bouquetRarity as RarityTier || 'common';

            const result = await storage.spawnButterflyOnFieldWithSlot(
              user.id, 
              placedBouquet.bouquetId, 
              rarity,
              currentSlot,
              4, // total attempts
              butterflyCount // already spawned count
            );

            if (result.success) {
              totalSpawns++;
              console.log(`‚ú® User ${user.id}: Butterfly spawned on field ${result.fieldIndex}: ${result.fieldButterfly?.butterflyName} from ${rarity} bouquet #${placedBouquet.bouquetId}! (Slot ${currentSlot}/4)`);

              // Advance to next spawn slot
              await storage.updateBouquetNextSpawnTime(user.id, placedBouquet.fieldIndex, new Date());
            } else {
              // Spawn failed due to probability check - still advance to next slot
              console.log(`üé≤ User ${user.id}: Spawn probability check failed for ${rarity} bouquet #${placedBouquet.bouquetId} (Slot ${currentSlot}/4)`);
              await storage.updateBouquetNextSpawnTime(user.id, placedBouquet.fieldIndex, new Date());
            }
          }
        } catch (error) {
          console.error(`ü¶ã Error checking bouquets for user ${user.id}:`, error);
        }
      }

      if (totalSpawns > 0) {
        console.log(`ü¶ã Individual spawn cycle complete: ${totalSpawns} butterflies spawned from ${totalChecked} bouquets checked`);
      } else {
        console.log(`ü¶ã Individual spawn cycle complete: No butterflies spawned (${totalChecked} bouquets checked)`);
      }

    } catch (error) {
      console.error('ü¶ã Error in individual butterfly spawn check:', error);
    }
  }

  // 4-Slot System: No longer needed as slots are managed by storage

  // Force a spawn check (for testing or manual triggers)
  async forceSpawnCheck() {
    console.log('ü¶ã Forcing individual butterfly spawn check...');
    await this.checkForButterflySpawns();
  }

  // 4-Slot System: Removed max spawns - now determined by slot completion

  getStatus() {
    return {
      isRunning: this.isRunning,
      hasActiveTimer: this.intervalId !== null
    };
  }
}

// Export singleton instance
export const butterflySpawner = new ButterflySpawner();

