    console.error('‚ùå NEW API Error placing butterfly:', error);
    res.status(500).json({ message: 'Internal server error' });
  }
});

// === PLACEHOLDER ROUTES (to be implemented) ===
app.get('/api/user/:userId/seeds', async (req, res) => {
