  challengeDonations,
  challengeRewards,
  unlockedFields,
  sunSpawns,
  pondFeedingProgressTable,
  fieldFish,
  aquariumTanks,
  aquariumFish,
  mariePosaTracker,
  dailyItems,
  dailyRedemptions,
  castleUnlockedParts,
  castleGridState,
  userUnlockedFeatures,
  collectionStats,
  userNotifications,
  type User, 
  type Seed, 
  type UserSeed, 
  type MarketListing,
  type PlantedField,
  type CreateMarketListingRequest,
  type BuyListingRequest,
  type PlantSeedRequest,
