  const rarityColors = {
    'common': '#FACC15',      // yellow-400
    'uncommon': '#4ADE80',    // green-400  
    'rare': '#60A5FA',        // blue-400
    'super-rare': '#22D3EE',  // cyan-400
    'epic': '#A855F7',        // purple-400
    'legendary': '#FB923C',   // orange-400
    'mythical': '#F87171'     // red-400
  };
  return rarityColors[rarity as keyof typeof rarityColors] || '#9CA3AF';
};

// PondFieldHover component with optimized performance and proper centering
const PondFieldHover: React.FC<{ 
  fieldId: number; 
  feedingProgress?: number;
  userId: number;
}> = React.memo(({ fieldId, feedingProgress, userId }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [data, setData] = useState<{ 
    averageRarity: string | null; 
    caterpillarCount: number; 
    caterpillarRarities: string[] 
  } | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const debounceRef = useRef<NodeJS.Timeout | null>(null);

  const fetchHoverData = useCallback(async () => {
    if (isLoading) return;

    // Check cache first
    const cached = pondHoverCache.get(fieldId);
    if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
      setData(cached.data);
      setIsOpen(true);
      return;
    }

    setIsLoading(true);

    try {
      const response = await fetch(`/api/user/${userId}/pond-field/${fieldId - 1}/average-rarity`);

      if (response.ok) {
        const result = await response.json();
        const hoverData = {
          averageRarity: result.averageRarity,
          caterpillarCount: result.caterpillarCount || 0,
          caterpillarRarities: result.caterpillarRarities || []
        };

        // Cache the result
        pondHoverCache.set(fieldId, { data: hoverData, timestamp: Date.now() });
        setData(hoverData);
        setIsOpen(true);
      }
    } catch (error) {
      if ((error as Error).name !== 'AbortError') {
        console.warn('Pond hover fetch failed:', error);
      }
    } finally {
      setIsLoading(false);
    }
  }, [fieldId, userId, isLoading]);

  const handlePointerEnter = useCallback(() => {
    if (debounceRef.current) {
      clearTimeout(debounceRef.current);
    }

    debounceRef.current = setTimeout(() => {
      fetchHoverData();
    }, 200); // 200ms debounce
  }, [fetchHoverData]);

  const handlePointerLeave = useCallback(() => {
    if (debounceRef.current) {
      clearTimeout(debounceRef.current);
    }
    setIsOpen(false);
  }, []);

  // Only show hover for pond fields with feeding progress 1-2
  if (!feedingProgress || feedingProgress <= 0 || feedingProgress >= 3) {
    return null;
  }

  return (
    <>
      {/* Invisible hover target covering the entire field */}
      <div 
        className="absolute inset-0 z-10 pointer-events-auto"
        onPointerEnter={handlePointerEnter}
        onPointerLeave={handlePointerLeave}
      />

      {/* Centered tooltip overlay */}
      {isOpen && data && (
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-50 pointer-events-none">
          <div 
            className="px-4 py-3 rounded-lg text-sm font-bold text-white shadow-xl border-2"
            style={{ 
              backgroundColor: data.averageRarity ? getRarityHexColor(data.averageRarity.toLowerCase()) : '#6B7280',
              borderColor: data.averageRarity ? getRarityHexColor(data.averageRarity.toLowerCase()) : '#9CA3AF',
              opacity: 0.95,
              minWidth: '120px',
              textAlign: 'center' as const
            }}
          >
            {data.averageRarity ? getRarityDisplayNameGerman(data.averageRarity) : 'Noch keine Raupen gef√ºttert'}
          </div>
        </div>
      )}
    </>
  );
});
