                        ? 'border-blue-500 bg-gradient-to-br from-blue-800/40 to-teal-800/40 cursor-pointer' 
                        : field.isUnlocked 
                          ? 'border-green-500 bg-green-900/20 hover:bg-green-900/40 active:bg-green-900/60 cursor-pointer' 
                          : isNextToUnlock 
                            ? 'border-orange-500 bg-slate-800 hover:bg-slate-700 active:bg-slate-600 opacity-50 cursor-pointer' 
                            : 'border-slate-600 bg-slate-800 opacity-40'
                      }
                      ${spinningFields.has(field.id - 1) ? 'animate-field-spin' : ''}
                      }
                      ${shakingField === field.id ? 'pond-shake' : ''}
                    `}
                    style={{
                      backgroundColor: field.isPond 
                        ? 'rgba(34, 118, 182, 0.2)' // Transparent blue for pond fields
                        : 'rgba(34, 197, 94, 0.1)', // Transparent green for grass fields
                      minHeight: '44px',
                      minWidth: '44px',
                      animation: shakingField === field.id ? 'pond-wobble 1.5s ease-in-out infinite' : 'none'
                    }}
                    onClick={() => {
                      if (field.isPond) {
                        // TEICHFELD: Wackelnd ‚Üí F√ºtterungs-Dialog, sonst Fish einsammeln
                        if (shakingField === field.id) {
                          // Open feeding dialog if user has caterpillars (only caterpillars can feed fish)
                          if (userCaterpillars.length > 0) {
                            console.log("üêõ Opening feeding dialog for pond field", field.id);
                            setSelectedField(field.id);
                            setShowFeedingDialog(true);
                          } else {
                            showNotification('Keine Futtermittel', 'Du hast keine Raupen zum F√ºttern im Inventar.', 'error');
                          }
                        } else {
                          // Try to collect fish from pond field (convert field.id to 0-based index)
                          const fishOnField = fieldFish.find(f => f.fieldIndex === field.id - 1);
                          if (fishOnField && !collectingFish.has(field.id)) {
                            console.log("üêü Attempting to collect fish on field", field.id);
                            collectFish(field.id);
                          } else if (collectingFish.has(field.id)) {
                            console.log("üêü Already collecting fish on field", field.id);
                          } else {
                            console.log("üêü Check for fish on pond field", field.id);
                          }
                        }
                        return;
                      }

                      // GRASFELD: Butterfly platzieren (KEINE Caterpillar-Logik hier - wird von Raupe onClick gehandelt)
                      if (!field.isPond) {
                        // Check if caterpillar is present - dann NICHT den Schmetterlings-Dialog √∂ffnen
                        const caterpillarOnField = fieldCaterpillars.find(c => c.fieldIndex === field.id - 1) || 
                                                  field.hasCaterpillar;

                        if (caterpillarOnField) {
                          console.log("üêõ Caterpillar present on field", field.id, "- ignoring field click");
                          return; // Caterpillar-Clicks werden von deren eigenem onClick gehandelt
                        }

                        // Check if temporary flower is present - dann NICHT den Dialog √∂ffnen
                        const temporaryFlowerOnField = placedFlowers.find(f => f.fieldId === field.id);

                        if (temporaryFlowerOnField) {
                          console.log("üå∏ Temporary flower present on field", field.id, "- ignoring field click");
                          return; // Feld mit Blume soll nicht anklickbar sein
                        }

                        // Blumen-Auswahl Dialog √∂ffnen f√ºr neue pond system (nur wenn keine Raupe UND keine Blume da ist)
                        console.log("üå∏ Debug: userFlowers.length =", userFlowers.length, "userButterflies.length =", userButterflies.length);
        console.log("üå∏ Debug: Full userFlowers =", userFlowers);
                        if (!isCollectingCaterpillar && userFlowers.length > 0) {
                          console.log("üå∏ Opening flower selection for field", field.id, "with", userFlowers.length, "flowers");
                          setSelectedField(field.id);
                          setShowFlowerModal(true);
                        } else if (isCollectingCaterpillar) {
                          console.log("üêõ Currently collecting caterpillar, skipping butterfly dialog for", field.id);
                        } else {
                          showNotification('Keine Blumen', 'Du hast keine Blumen im Inventar zum Platzieren.', 'info');
                        }
                        return;
                      }
                    }}
                    onContextMenu={(e) => {
                      e.preventDefault();
                      if (field.isPond) return;
                      if (field.isUnlocked && !field.hasBouquet) {
                        setSelectedField(field.id);
                        setShowBouquetModal(true);
                      }
                    }}
                  >

                    {/* Pond indicator */}
                    {field.isPond && (
                      <div className="absolute inset-0 flex items-center justify-center">
                        <Waves className="h-6 w-6 text-blue-300/80" />
                      </div>
                    )}

                    {/* No sun spawns in TeichView at all */}
                    {false && field.hasSunSpawn && field.sunSpawnAmount && !field.isPond && (
                      <Tooltip>
                        <TooltipTrigger asChild>
                          <div className="absolute inset-0 flex flex-col items-center justify-center bg-yellow-400/20 rounded-lg border border-yellow-400/50 animate-pulse cursor-pointer hover:bg-yellow-400/30">
                            <Sun className="h-5 w-5 text-yellow-400" />
                            <span className="text-xs font-bold text-yellow-300">+{field.sunSpawnAmount}</span>
                          </div>
                        </TooltipTrigger>
                        <TooltipContent>
                          <p>Klicken zum Einsammeln: +{field.sunSpawnAmount} Sonnen</p>
                          {field.sunSpawnExpiresAt && (
                            <p className="text-xs text-red-300">
                              L√§uft ab in {Math.max(0, Math.ceil((field.sunSpawnExpiresAt.getTime() - Date.now()) / 1000))}s
                            </p>
                          )}
                        </TooltipContent>
                      </Tooltip>
                    )}


                    {/* Persistent field flowers from database (ALWAYS VISIBLE) */}
                    {fieldFlowers.find(f => f.fieldIndex === field.id - 1) && (() => {
                      const dbFlower = fieldFlowers.find(f => f.fieldIndex === field.id - 1)!;
                      return (
                        <div className="absolute inset-0 flex items-center justify-center cursor-pointer">
                          <RarityImage
                            src={dbFlower.flowerImageUrl}
                            alt={dbFlower.flowerName || "Blume"}
                            rarity={dbFlower.flowerRarity as RarityTier || "common"}
                            size="large"
                            className="w-full h-full"
                          />
                        </div>
                      );
                    })()}

                    {/* Local placed flowers with shimmer and dissolve animations */}
                    {placedFlowers.find(f => f.fieldId === field.id) && (() => {
                      const flower = placedFlowers.find(f => f.fieldId === field.id)!;
                      return (
                        <div 
                          className={`absolute inset-0 flex items-center justify-center cursor-pointer ${
                            flower.isShimmering ? 'animate-shimmer' : ''
                          } ${
                            flower.isDissolving ? 'animate-dissolve' : ''
                          }`}
                        >
                          <RarityImage
                            src={flower.flowerImageUrl}
                            alt={flower.flowerName || "Blume"}
                            rarity={flower.flowerRarity as RarityTier || "common"}
                            size="large"
                            className="w-full h-full"
                          />
                        </div>
                      );
                    })()}

                    {/* Butterflies are only visible in garden view, not in pond view */}
                    {false && (
                      <ButterflyHoverPreview
                        butterflyId={field.butterflyId!}
                        butterflyName={field.butterflyName!}
                        butterflyImageUrl={field.butterflyImageUrl!}
                        rarity={field.butterflyRarity as RarityTier}
                      >
                        <div 
                          className="absolute inset-0 flex items-center justify-center cursor-pointer hover:scale-110 transition-all"
                          onClick={() => collectButterfly(field.id)}
                        >
                          <RarityImage
                            src={field.butterflyImageUrl!}
                            alt={field.butterflyName || "Schmetterling"}
                            rarity={field.butterflyRarity as RarityTier || "common"}
                            size="large"
                            className="w-full h-full"
                          />
                        </div>
                      </ButterflyHoverPreview>
                    )}

                    {/* Real Field Caterpillars from Database (PERMANENT - highest priority) */}
                    {fieldCaterpillars
                      .filter(c => c.fieldIndex === field.id - 1)
                      .map(caterpillar => (
                      <CaterpillarHoverPreview
                        key={`real-caterpillar-${caterpillar.id}`}
                        caterpillarId={caterpillar.caterpillarId}
                        caterpillarName={caterpillar.caterpillarName}
                        caterpillarImageUrl={caterpillar.caterpillarImageUrl}
                        rarity={caterpillar.caterpillarRarity as RarityTier}
                      >
                        <div 
                          className="absolute inset-0 flex items-center justify-center cursor-pointer hover:scale-110 transition-transform z-30 animate-fade-in"
                          onClick={(e) => {
                            e.stopPropagation(); // Verhindert Event-Bubbling zum Feld
                            setIsCollectingCaterpillar(true); // Sofort blockieren
                            collectCaterpillar(field.id - 1);
                          }}
                        >
                          <RarityImage
                            src={caterpillar.caterpillarImageUrl}
                            alt={caterpillar.caterpillarName || "Raupe"}
                            rarity={caterpillar.caterpillarRarity as RarityTier || "common"}
                            size="large"
                            className="w-full h-full field-image"
                          />
                        </div>
                      </CaterpillarHoverPreview>
                    ))}


                    {/* Field Caterpillar with Bounce Effect - hide during butterfly animation, local caterpillars AND spawned caterpillars */}
                    {field.hasCaterpillar && field.caterpillarImageUrl && (
                      <CaterpillarHoverPreview
                        caterpillarId={field.caterpillarId!}
                        caterpillarName={field.caterpillarName!}
                        caterpillarImageUrl={field.caterpillarImageUrl}
                        rarity={field.caterpillarRarity as RarityTier}
                      >
                        <div 
                          className="absolute inset-0 flex items-center justify-center cursor-pointer transition-all hover:scale-110 animate-bounce-spawn"
                          onClick={(e) => {
                            e.stopPropagation(); // Verhindert Event-Bubbling zum Feld
                            setIsCollectingCaterpillar(true); // Sofort blockieren
                            collectCaterpillar(field.id - 1);
                          }}
                          style={{
                            animation: 'bounce-spawn 0.8s cubic-bezier(0.68, -0.55, 0.265, 1.55) forwards'
                          }}
                        >
                          <RarityImage
                            src={field.caterpillarImageUrl}
                            alt={field.caterpillarName || "Raupe"}
                            rarity={field.caterpillarRarity as RarityTier || "common"}
                            size="large"
                            className="w-full h-full field-image"
                          />
                        </div>
                      </CaterpillarHoverPreview>
                    )}

                    {/* Bouquet - disabled in TeichView */}
                    {false && field.hasBouquet && (
                      <div className="absolute inset-0 flex flex-col items-center justify-center">
                        <RarityImage
                          src="/Blumen/Bouquet.jpg"
                          alt={field.bouquetName || "Bouquet"}
                          rarity={field.bouquetRarity as RarityTier || "common"}
                          size="small"
                          className="w-10 h-10"
                        />
                        {field.bouquetExpiresAt && (
                          <div className="absolute -bottom-1 text-xs text-white/80 bg-black/60 px-1 rounded">
                            {Math.max(0, Math.ceil((field.bouquetExpiresAt.getTime() - Date.now()) / (1000 * 60)))}m
                          </div>
                        )}
                      </div>
                    )}

                    {/* Plant/Seed */}
                    {field.hasPlant && (
                      <div className="absolute inset-0 flex flex-col items-center justify-center">
                        {field.isGrowing ? (
                          <Tooltip>
                            <TooltipTrigger asChild>
                              <div className="flex flex-col items-center">
                                <RarityImage
                                  src="/Blumen/0.jpg"
                                  alt="Wachsender Samen"
                                  rarity={field.seedRarity as RarityTier || "common"}
                                  size="small"
                                  className="w-8 h-8"
                                />
                                <div className="text-xs text-white/80 mt-1 bg-black/60 px-1 rounded flex items-center">
                                  <Clock className="h-3 w-3 mr-1" />
                                  {field.plantedAt && field.growthTimeSeconds && (
                                    <>
                                      {(() => {
                                        const now = Date.now();
                                        const plantTime = field.plantedAt.getTime();
                                        const growthMs = field.growthTimeSeconds * 1000;
                                        const remainingMs = Math.max(0, (plantTime + growthMs) - now);
                                        return formatTime(Math.ceil(remainingMs / 1000));
                                      })()}
                                    </>
                                  )}
                                </div>
                              </div>
                            </TooltipTrigger>
                            <TooltipContent>
                              <p>{field.flowerName || "Unbekannte Blume"}</p>
                              <p className="text-sm text-slate-300">
                                W√§chst noch...
                              </p>
                            </TooltipContent>
                          </Tooltip>
                        ) : (
                          <FlowerHoverPreview
                            flowerId={field.flowerId!}
                            flowerName={field.flowerName!}
                            flowerImageUrl={field.flowerImageUrl!}
                            rarity={field.seedRarity as RarityTier}
                          >
                            <div className="flex flex-col items-center cursor-pointer hover:scale-110 transition-transform">
                              <RarityImage
                                src={field.flowerImageUrl!}
                                alt={field.flowerName || "Blume"}
                                rarity={field.seedRarity as RarityTier || "common"}
                                size="small"
                                className="w-10 h-10"
                              />
                              <div className="absolute -bottom-2 bg-green-600 text-white text-xs px-2 py-0.5 rounded-full flex items-center animate-bounce">
                                <Flower className="h-3 w-3 mr-1" />
                                +1 Blume!
                              </div>
                            </div>
                          </FlowerHoverPreview>
                        )}
                      </div>
                    )}


                    {/* Placed Fish Display */}
                    {placedFish
                      .filter(fish => fish.fieldId === field.id)
                      .map(fish => (
                        <div
                          key={fish.id}
                          className={`absolute inset-0 flex items-center justify-center transition-all duration-500 ${
                            fish.isShrinkling ? 'animate-pulse opacity-50 scale-75' : 'opacity-100 scale-100'
                          }`}
                          style={{
                            transform: fish.isShrinkling ? 'scale(0.7)' : 'scale(1)',
                            transition: 'all 0.5s ease-in-out'
                          }}
                        >
                          <FishHoverPreview
                            fishImageUrl={fish.fishImageUrl}
                            fishName={fish.fishName}
                            rarity={fish.fishRarity as RarityTier}
                          >
                            <div
                              className="w-8 h-8 rounded-full border-2 cursor-pointer hover:scale-110 transition-transform"
                              style={{ borderColor: getFishBorderColor(fish.fishRarity) }}
                            >
                              <img
                                src={fish.fishImageUrl}
                                alt={fish.fishName}
                                className="w-full h-full object-cover rounded-full"
                                onError={(e) => {
                                  e.currentTarget.style.display = 'none';
                                  e.currentTarget.nextElementSibling!.style.display = 'block';
                                }}
                              />
                              <div
                                className="w-full h-full bg-blue-500 rounded-full flex items-center justify-center"
                                style={{ display: 'none' }}
                              >
                                üêü
                              </div>
                            </div>
                          </FishHoverPreview>
                        </div>
                      ))}

                    {/* Field Fish - Spawned from feeding (bouncing animation) */}
                    {field.hasFish && (
                      <div 
                        className={`absolute inset-0 flex items-center justify-center cursor-pointer group z-30 ${
                          collectingFish.has(field.id) 
                            ? 'opacity-0 scale-50 transition-all duration-300' 
                            : 'animate-bounce opacity-100 scale-100'
                        }`}
                        onClick={(e) => {
                          e.stopPropagation(); // Prevent field click handler from also firing
                          if (!collectingFish.has(field.id)) {
                            collectFish(field.id);
                          }
                        }}
                      >
                        <div className={`relative transform transition-transform duration-200 ${
                          collectingFish.has(field.id) ? '' : 'group-hover:scale-110'
                        }`}>
                          <RarityImage 
                            src={field.fishImageUrl!}
                            alt={field.fishName || "Fisch"}
                            rarity={field.fishRarity as RarityTier || "common"}
                            size="large"
                            className="w-full h-full"
                          />
                          <div className={`absolute -top-1 -right-1 bg-cyan-400 text-white text-xs px-1 py-0.5 rounded-full flex items-center transition-opacity duration-300 ${
                            collectingFish.has(field.id) ? 'opacity-0' : 'animate-pulse opacity-100'
                          }`}>
                            üêü
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Field Caterpillars - Spawned from butterflies (full field size) */}
                    {field.hasCaterpillar && (
                      <div 
                        className="absolute inset-0 flex items-center justify-center cursor-pointer group animate-pulse z-30"
                        onClick={() => handleCaterpillarClick(field)}
                      >
                        <CaterpillarHoverPreview
                          caterpillarImageUrl={field.caterpillarImageUrl!}
                          caterpillarName={field.caterpillarName!}
                          rarity={field.caterpillarRarity as RarityTier}
                          className="z-10"
                        >
                          <div className="relative transform group-hover:scale-110 transition-transform duration-200">
                            <RarityImage 
                              src={field.caterpillarImageUrl!}
                              alt={field.caterpillarName || "Raupe"}
                              rarity={field.caterpillarRarity as RarityTier || "common"}
                              size="large"
                              className="w-full h-full"
                            />
                            <div className="absolute -top-1 -right-1 bg-green-400 text-white text-xs px-1 py-0.5 rounded-full flex items-center animate-bounce">
                              üêõ
                            </div>
                          </div>
                        </CaterpillarHoverPreview>
                      </div>
                    )}

                    {/* Pond Feeding Progress Icons - Show fish symbols for feeding progress */}
                    {field.isPond && field.feedingProgress && field.feedingProgress > 0 && field.feedingProgress < 3 && (
                      <div className="absolute inset-0 flex items-center justify-center z-20 pointer-events-none">
                        {field.feedingProgress === 1 && (
                          <div className="text-6xl animate-pulse drop-shadow-lg filter drop-shadow-[0_2px_8px_rgba(59,130,246,0.5)]">üêü</div>
                        )}
                        {field.feedingProgress === 2 && (
                          <div className="text-6xl animate-bounce drop-shadow-lg filter drop-shadow-[0_2px_8px_rgba(34,197,94,0.5)]">üê†</div>
                        )}
                      </div>
                    )}

                    {/* Optimized pond field hover with performance and centering fixes */}
                    {field.isPond && user && (
                      <PondFieldHover 
                        fieldId={field.id}
                        feedingProgress={field.feedingProgress}
                        userId={user.id}
                      />
                    )}


                  </div>
                );
              })}
            </div>
          </div>
          </CardContent>
        </Card>

        {/* Modals */}
        <SeedSelectionModal
          isOpen={showSeedModal}
          onClose={() => {
            setShowSeedModal(false);
            setSelectedField(null);
          }}
          seeds={userSeeds || []}
          onSelectSeed={plantSeed}
          fieldIndex={selectedField || 0}
        />

        <BouquetSelectionModal
          isOpen={showBouquetModal}
          onClose={() => {
            setShowBouquetModal(false);
            setSelectedField(null);
          }}
          userBouquets={userBouquets || []}
          onPlaceBouquet={placeBouquet}
          fieldIndex={selectedField || 0}
        />

        {/* Butterfly Selection Modal - DISABLED f√ºr direkte Platzierung */}
        <ButterflySelectionModal
          isOpen={showButterflyModal}
          onClose={() => {
            setShowButterflyModal(false);
            setSelectedField(null);
          }}
          userButterflies={userButterflies}
          onSelectButterfly={placeButterflyOnField}
          fieldIndex={selectedField || 0}
        />

        <FlowerSelectionModal
          isOpen={showFlowerModal}
          onClose={() => {
            setShowFlowerModal(false);
            setSelectedField(null);
          }}
          userFlowers={userFlowers}
          onSelectFlower={placeFlowerOnField}
          fieldIndex={selectedField || 0}
        />

        {/* Fish Selection Modal */}
        <FishSelectionModal
          isOpen={showFishModal}
          onClose={() => {
            setShowFishModal(false);
            setSelectedField(null);
          }}
          onFishSelected={handleFishSelection}
        />

        {/* Feeding Dialog */}
        <FeedingDialog
          isOpen={showFeedingDialog}
          onClose={() => {
            setShowFeedingDialog(false);
            setSelectedField(null);
          }}
          caterpillars={userCaterpillars}
          onFeedCaterpillar={onFeedCaterpillar}
          fieldIndex={(selectedField || 1) - 1}
        />

        {/* Caterpillar Selection Modal removed - they spawn automatically */}

        {/* Fish Reward Dialog */}
        <FishRewardDialog
          isOpen={isFishRewardDialogOpen}
          onClose={() => setIsFishRewardDialogOpen(false)}
          fishName={fishRewardData?.fishName || ""}
          fishImageUrl={fishRewardData?.fishImageUrl || ""}
          rarity={fishRewardData?.rarity || "common"}
        />
      </TooltipProvider>
    </div>
  );
};

client/src/components/ButterflySelection.content:
import React from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './ui/dialog';
import { Button } from './ui/button';
import { ScrollArea } from './ui/scroll-area';
import { getRarityColor, getRarityDisplayName } from "@shared/rarity";
import { RarityImage } from './RarityImage';
import { Sparkles } from 'lucide-react';

interface ButterflySelectionModalProps {
  isOpen: boolean;
  onClose: () => void;
  userButterflies: any[];
  onSelectButterfly: (butterflyId: number) => void;
  fieldIndex: number;
}

export const ButterflySelectionModal: React.FC<ButterflySelectionModalProps> = ({
  isOpen,
  onClose,
  userButterflies,
  onSelectButterfly,
  fieldIndex
}) => {
  // Filter out butterflies with quantity 0
  const availableButterflies = userButterflies.filter(butterfly => butterfly.quantity > 0);

  const handleSelectButterfly = (butterflyId: number) => {
    onSelectButterfly(butterflyId);
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px] max-h-[80vh] bg-slate-900 border-slate-700">
        <DialogHeader>
          <DialogTitle className="text-white flex items-center gap-2">
            <Sparkles className="h-5 w-5 text-yellow-500" />
            Schmetterling platzieren - Feld {fieldIndex}
          </DialogTitle>
        </DialogHeader>

        <ScrollArea className="max-h-[60vh]">
          <div className="space-y-3 p-1">
            {availableButterflies.length === 0 ? (
              <div className="text-center text-slate-400 py-8">
                <Sparkles className="h-12 w-12 mx-auto mb-4 text-slate-500" />
                <p>Keine Schmetterlinge im Inventar</p>
                <p className="text-sm mt-2">Besuche die Blumen-Ansicht um Schmetterlinge zu sammeln!</p>
              </div>
            ) : (
              availableButterflies.map((butterfly) => (
                <Button
                  key={butterfly.id}
                  onClick={() => handleSelectButterfly(butterfly.id)}
                  className="w-full h-auto p-4 bg-slate-800 hover:bg-slate-700 border border-slate-600 text-left"
                  variant="ghost"
                >
                  <div className="flex items-center space-x-4 w-full">
                    <div className="relative">
                      <RarityImage
                        src={butterfly.butterflyImageUrl}
                        alt={butterfly.butterflyName}
                        rarity={butterfly.butterflyRarity}
                        size="medium"
                        className="w-14 h-14 rounded-lg"
                      />
                    </div>

                    <div className="flex-1 text-left">
                      <h4 className="font-semibold text-white text-base mb-1">
                        {butterfly.butterflyName}
                      </h4>
                      <p className={`text-sm font-medium ${getRarityColor(butterfly.butterflyRarity)}`}>
                        {getRarityDisplayName(butterfly.butterflyRarity)}
                      </p>
                      <div className="flex items-center gap-2 mt-2">
                        <span className="bg-green-600 text-white text-xs px-2 py-1 rounded font-bold">
                          x{butterfly.quantity}
                        </span>
                        <span className="text-xs text-slate-400">verf√ºgbar</span>
                      </div>
                    </div>

                    <div className="text-right">
                      <div className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-2 rounded text-sm font-medium">
                        Platzieren
                      </div>
                    </div>
                  </div>
                </Button>
              ))
            )}
          </div>
        </ScrollArea>

        <div className="flex justify-end pt-4">
          <Button
            onClick={onClose}
            variant="outline"
            className="border-slate-600 text-slate-300 hover:text-white hover:border-slate-500"
          >
            Abbrechen
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
};

server/creatures.ts.content:
import { RarityTier } from "@shared/rarity";

// Fish Data Interface
export interface FishData {
  id: number;
  name: string;
  imageUrl: string;
}

// Caterpillar Data Interface  
export interface CaterpillarData {
  id: number;
  name: string;
  imageUrl: string;
}

// ==================== FISH SYSTEM ====================

// Generate all available fish IDs dynamically from Fische folder
async function generateAvailableFishIds(): Promise<number[]> {
  try {
    const fs = await import('fs/promises');
    const path = await import('path');

    const fishDir = path.join(process.cwd(), 'client/public/Fische');
    const files = await fs.readdir(fishDir);

    // Extract fish numbers from xxx.jpg files
    const ids: number[] = [];
    files.forEach(file => {
      const match = file.match(/^(\d+)\.jpg$/i);
      if (match) {
        const id = parseInt(match[1]);
        if (!isNaN(id)) {
          ids.push(id);
        }
      }
    });

    // Sort IDs numerically
    const sortedIds = ids.sort((a, b) => a - b);
    console.log(`üêü Found ${sortedIds.length} fish images: ${Math.min(...sortedIds)}-${Math.max(...sortedIds)}`);

    return sortedIds.length > 0 ? sortedIds : [1]; // Fallback to just 1 if none found
  } catch (error) {
    console.error('üêü Error reading Fische directory:', error);
    // Fallback to original range if scanning fails
    const ids: number[] = [];
    for (let i = 1; i <= 15; i++) {
      ids.push(i);
    }
    return ids;
  }
}

// Initialize available fish IDs
let AVAILABLE_FISH_IDS: number[] = [];
let TOTAL_FISH = 15; // Default, will be updated

// Get proper filename for fish ID
function getFishImageFilename(id: number): string {
  return `${id}.jpg`;
}

// Base rarity distribution for fish (same percentages as butterflies)
const FISH_RARITY_DISTRIBUTION_PERCENT = {
  common: 0.443,      // 44.3%
  uncommon: 0.30,     // 30.0%
  rare: 0.122,        // 12.2%
  'super-rare': 0.078, // 7.8%
  epic: 0.047,        // 4.7%
  legendary: 0.026,   // 2.6%
  mythical: 0.013     // 1.3%
};

// Dynamic fish rarity distribution
let FISH_RARITY_DISTRIBUTION = {};

// Create randomized rarity assignments for each fish ID
export const FISH_RARITY_MAP = new Map<number, RarityTier>();

// Fisher-Yates shuffle algorithm
function shuffleArray<T>(array: T[]): T[] {
  const shuffled = [...array];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled;
}

// Initialize fish rarity assignments
async function initializeFishRarities(): Promise<void> {
  // Get available fish IDs
  AVAILABLE_FISH_IDS = await generateAvailableFishIds();
  TOTAL_FISH = AVAILABLE_FISH_IDS.length;

  // Calculate rarity distribution based on total fish
  FISH_RARITY_DISTRIBUTION = {
    common: Math.floor(TOTAL_FISH * FISH_RARITY_DISTRIBUTION_PERCENT.common),
    uncommon: Math.floor(TOTAL_FISH * FISH_RARITY_DISTRIBUTION_PERCENT.uncommon),
    rare: Math.floor(TOTAL_FISH * FISH_RARITY_DISTRIBUTION_PERCENT.rare),
    'super-rare': Math.floor(TOTAL_FISH * FISH_RARITY_DISTRIBUTION_PERCENT['super-rare']),
    epic: Math.floor(TOTAL_FISH * FISH_RARITY_DISTRIBUTION_PERCENT.epic),
    legendary: Math.floor(TOTAL_FISH * FISH_RARITY_DISTRIBUTION_PERCENT.legendary),
    mythical: Math.floor(TOTAL_FISH * FISH_RARITY_DISTRIBUTION_PERCENT.mythical)
  };

  // Ensure we use all fish by adjusting the common tier
  const totalAssigned = Object.values(FISH_RARITY_DISTRIBUTION).reduce((sum: number, count) => sum + (count as number), 0);
  (FISH_RARITY_DISTRIBUTION as any).common += (TOTAL_FISH - totalAssigned);

  // Create rarity assignments
  const rarities: RarityTier[] = [];
  (Object.entries(FISH_RARITY_DISTRIBUTION) as [RarityTier, number][]).forEach(([rarity, count]) => {
    for (let i = 0; i < count; i++) {
      rarities.push(rarity);
    }
  });

  // Shuffle rarities for randomness
  const shuffledRarities = shuffleArray(rarities);

  // Assign rarities to fish IDs
  AVAILABLE_FISH_IDS.forEach((fishId, index) => {
    FISH_RARITY_MAP.set(fishId, shuffledRarities[index] || 'common');
  });

  // Debug logging f√ºr Fish Rarity-Verteilung
  const fishRarityCounts: Record<string, number> = {};
  FISH_RARITY_MAP.forEach(rarity => {
    fishRarityCounts[rarity] = (fishRarityCounts[rarity] || 0) + 1;
  });

  console.log(`üêü Initialized fish system with ${TOTAL_FISH} fish`);
  console.log(`üêü Fish rarity distribution:`, fishRarityCounts);
}

// Generate random fish of specific rarity
export async function generateRandomFish(rarity: RarityTier): Promise<FishData> {
  console.log(`üêü DEBUG: Generating fish with rarity: ${rarity}`);
  console.log(`üêü DEBUG: Available fish IDs:`, AVAILABLE_FISH_IDS.slice(0, 10), `(total: ${AVAILABLE_FISH_IDS.length})`);
  console.log(`üêü DEBUG: Fish rarity map size:`, FISH_RARITY_MAP.size);

  // Get all fish IDs that match the requested rarity
  const matchingIds = Array.from(FISH_RARITY_MAP.entries())
    .filter(([id, assignedRarity]) => assignedRarity === rarity)
    .map(([id]) => id);

  console.log(`üêü DEBUG: Matching IDs for ${rarity}:`, matchingIds.slice(0, 5), `(total: ${matchingIds.length})`);

  // If no fish of this rarity, fall back to any available ID
  const availableIds = matchingIds.length > 0 ? matchingIds : AVAILABLE_FISH_IDS;
  console.log(`üêü DEBUG: Using ${availableIds.length} available IDs`);

  if (availableIds.length === 0) {
    console.error('üêü ERROR: No available fish IDs found!');
    // Emergency fallback - create a default fish
    return {
      id: 0,
      name: 'Notfall-Fisch',
      imageUrl: '/Fische/0.jpg'
    };
  }

  const fishId = availableIds[Math.floor(Math.random() * availableIds.length)];
  console.log(`üêü DEBUG: Selected fishId: ${fishId}`);

  // Generate consistent Latin name using fishId as seed
  const { generateLatinFishName } = await import('../shared/rarity');
  const name = generateLatinFishName(fishId);
  console.log(`üêü DEBUG: Generated name: ${name}`);

  const fishData = {
    id: fishId,
    name,
    imageUrl: `/Fische/${getFishImageFilename(fishId)}`
  };

  console.log(`üêü DEBUG: Final fish data:`, fishData);
  return fishData;
}

// Generate random fish using proper distribution
export async function generateRandomFishByDistribution(rarity: RarityTier): Promise<FishData> {
  return generateRandomFish(rarity);
}

// Get random rarity based on distribution
export function getRandomRarity(): RarityTier {
  const random = Math.random();
  let cumulativeWeight = 0;

  for (const [rarity, percentage] of Object.entries(FISH_RARITY_DISTRIBUTION_PERCENT) as [RarityTier, number][]) {
    cumulativeWeight += percentage;
    if (random <= cumulativeWeight) {
      return rarity;
    }
  }

  return 'common'; // Fallback
}

// ==================== CATERPILLAR SYSTEM ====================

// Generate all available caterpillar IDs dynamically from Raupen folder
async function generateAvailableCaterpillarIds(): Promise<number[]> {
  try {
    const fs = await import('fs/promises');
    const path = await import('path');

    const caterpillarDir = path.join(process.cwd(), 'client/public/Raupen');
    const files = await fs.readdir(caterpillarDir);

    // Extract caterpillar numbers from xxx.jpg files (not png!)
    const ids: number[] = [];
    files.forEach(file => {
      const match = file.match(/^(\d+)\.jpg$/i);
      if (match) {
        const id = parseInt(match[1]);
        if (!isNaN(id)) {
          ids.push(id);
        }
      }
    });

    // Sort IDs numerically
    const sortedIds = ids.sort((a, b) => a - b);
    console.log(`üêõ Found ${sortedIds.length} caterpillar images: ${Math.min(...sortedIds)}-${Math.max(...sortedIds)}`);

    return sortedIds.length > 0 ? sortedIds : [1]; // Fallback to just 1 if none found
  } catch (error) {
    console.error('üêõ Error reading Raupen directory:', error);
    // Fallback to original range if scanning fails
    const ids: number[] = [];
    for (let i = 1; i <= 20; i++) {
      ids.push(i);
    }
    return ids;
  }
}

// Initialize available caterpillar IDs
let AVAILABLE_CATERPILLAR_IDS: number[] = [];
let TOTAL_CATERPILLARS = 20; // Default, will be updated

// Get proper filename for caterpillar ID
function getCaterpillarImageFilename(id: number): string {
  return `${id}.jpg`;
}

// Dynamic caterpillar rarity distribution (same percentages)
let CATERPILLAR_RARITY_DISTRIBUTION = {};

// Create randomized rarity assignments for each caterpillar ID
export const CATERPILLAR_RARITY_MAP = new Map<number, RarityTier>();

// Initialize caterpillar rarity assignments
async function initializeCaterpillarRarities(): Promise<void> {
  // Get available caterpillar IDs
  AVAILABLE_CATERPILLAR_IDS = await generateAvailableCaterpillarIds();
  TOTAL_CATERPILLARS = AVAILABLE_CATERPILLAR_IDS.length;

  // Calculate rarity distribution based on total caterpillars
  CATERPILLAR_RARITY_DISTRIBUTION = {
    common: Math.floor(TOTAL_CATERPILLARS * FISH_RARITY_DISTRIBUTION_PERCENT.common),
    uncommon: Math.floor(TOTAL_CATERPILLARS * FISH_RARITY_DISTRIBUTION_PERCENT.uncommon),
    rare: Math.floor(TOTAL_CATERPILLARS * FISH_RARITY_DISTRIBUTION_PERCENT.rare),
    'super-rare': Math.floor(TOTAL_CATERPILLARS * FISH_RARITY_DISTRIBUTION_PERCENT['super-rare']),
    epic: Math.floor(TOTAL_CATERPILLARS * FISH_RARITY_DISTRIBUTION_PERCENT.epic),
    legendary: Math.floor(TOTAL_CATERPILLARS * FISH_RARITY_DISTRIBUTION_PERCENT.legendary),
    mythical: Math.floor(TOTAL_CATERPILLARS * FISH_RARITY_DISTRIBUTION_PERCENT.mythical)
  };

  // Ensure we use all caterpillars by adjusting the common tier
  const totalAssigned = Object.values(CATERPILLAR_RARITY_DISTRIBUTION).reduce((sum: number, count) => sum + (count as number), 0);
  (CATERPILLAR_RARITY_DISTRIBUTION as any).common += (TOTAL_CATERPILLARS - totalAssigned);

  // Create rarity assignments
  const rarities: RarityTier[] = [];
  (Object.entries(CATERPILLAR_RARITY_DISTRIBUTION) as [RarityTier, number][]).forEach(([rarity, count]) => {
    for (let i = 0; i < count; i++) {
      rarities.push(rarity);
    }
  });

  // Shuffle rarities for randomness
  const shuffledRarities = shuffleArray(rarities);

  // Assign rarities to caterpillar IDs
  AVAILABLE_CATERPILLAR_IDS.forEach((caterpillarId, index) => {
    CATERPILLAR_RARITY_MAP.set(caterpillarId, shuffledRarities[index] || 'common');
  });

  // Debug logging f√ºr Rarity-Verteilung
  const rarityCounts: Record<string, number> = {};
  CATERPILLAR_RARITY_MAP.forEach(rarity => {
    rarityCounts[rarity] = (rarityCounts[rarity] || 0) + 1;
  });

  console.log(`üêõ Initialized caterpillar system with ${TOTAL_CATERPILLARS} caterpillars`);
  console.log(`üêõ Caterpillar rarity distribution:`, rarityCounts);
}

// Generate random caterpillar of specific rarity
export async function generateRandomCaterpillar(rarity: RarityTier): Promise<CaterpillarData> {
  // Get all caterpillar IDs that match the requested rarity
  const matchingIds = Array.from(CATERPILLAR_RARITY_MAP.entries())
    .filter(([id, assignedRarity]) => assignedRarity === rarity)
    .map(([id]) => id);

  // If no caterpillars of this rarity, fall back to any available ID
  const availableIds = matchingIds.length > 0 ? matchingIds : AVAILABLE_CATERPILLAR_IDS;
  const caterpillarId = availableIds[Math.floor(Math.random() * availableIds.length)];

  // Generate consistent Latin name using caterpillarId as seed
  const { generateLatinCaterpillarName } = await import('../shared/rarity');
  const name = generateLatinCaterpillarName(caterpillarId);

  return {
    id: caterpillarId,
    name,
    imageUrl: `/Raupen/${getCaterpillarImageFilename(caterpillarId)}`
  };
}

// ==================== INITIALIZATION ====================

// Initialize both systems
// ==================== FLOWER SYSTEM ====================

// Initialize available flower IDs
let AVAILABLE_FLOWER_IDS: number[] = [];
let TOTAL_FLOWERS = 200; // Default, will be updated

// Get proper filename for flower ID
function getFlowerImageFilename(id: number): string {
  return `${id}.jpg`;
}

// Base rarity distribution for flowers (same percentages as fish/caterpillars)
const FLOWER_RARITY_DISTRIBUTION_PERCENT = {
  common: 0.275,       // 27.5% (55/200)
  uncommon: 0.225,     // 22.5% (45/200)
  rare: 0.175,         // 17.5% (35/200)
  'super-rare': 0.125, // 12.5% (25/200)
  epic: 0.10,          // 10.0% (20/200)
  legendary: 0.075,    // 7.5%  (15/200)
  mythical: 0.025      // 2.5%  (5/200)
};

// Dynamic flower rarity distribution
let FLOWER_RARITY_DISTRIBUTION = {};

// Create randomized rarity assignments for each flower ID
export const FLOWER_RARITY_MAP = new Map<number, RarityTier>();

// Store original flower rarities (1-200) to preserve existing assignments
let ORIGINAL_FLOWER_RARITIES: Map<number, RarityTier> | null = null;

// Generate all available flower IDs dynamically from Blumen folder
async function generateAvailableFlowerIds(): Promise<number[]> {
  try {
    const fs = await import('fs/promises');
    const path = await import('path');

    const flowerDir = path.join(process.cwd(), 'client/public/Blumen');
    const files = await fs.readdir(flowerDir);

    // Extract flower numbers from xxx.jpg files
    const ids: number[] = [];
    files.forEach(file => {
      const match = file.match(/^(\d+)\.jpg$/i);
      if (match) {
        const id = parseInt(match[1]);
        if (!isNaN(id)) {
          ids.push(id);
        }
      }
    });

    // Sort IDs numerically
    const sortedIds = ids.sort((a, b) => a - b);
    console.log(`üå∏ Found ${sortedIds.length} flower images: ${Math.min(...sortedIds)}-${Math.max(...sortedIds)}`);

    return sortedIds.length > 0 ? sortedIds : [1]; // Fallback to just 1 if none found
  } catch (error) {
    console.error('üå∏ Error reading Blumen directory:', error);
    // Fallback to original range from RARITY_CONFIG if scanning fails
    const ids: number[] = [];
    for (let i = 1; i <= 200; i++) {
      ids.push(i);
    }
    return ids;
  }
}

// Initialize flower rarity assignments
async function initializeFlowerRarities(): Promise<void> {
  try {
    // Get available flower IDs
    AVAILABLE_FLOWER_IDS = await generateAvailableFlowerIds();
    TOTAL_FLOWERS = AVAILABLE_FLOWER_IDS.length;

    // Preserve original rarities for existing flowers (1-200) from RARITY_CONFIG
    if (!ORIGINAL_FLOWER_RARITIES) {
      ORIGINAL_FLOWER_RARITIES = new Map();

      // Import RARITY_CONFIG to get original flower rarities
      const { RARITY_CONFIG } = await import('../shared/rarity');

      // Create original rarity assignments for flowers 1-200 based on RARITY_CONFIG
      for (let flowerId = 1; flowerId <= 200; flowerId++) {
        if (AVAILABLE_FLOWER_IDS.includes(flowerId)) {
          let rarity: RarityTier = 'common'; // Default fallback

          // Determine rarity based on RARITY_CONFIG ranges
          if (flowerId >= 1 && flowerId <= 55) {
            rarity = 'common';
          } else if (flowerId >= 56 && flowerId <= 100) {
            rarity = 'uncommon';
          } else if (flowerId >= 101 && flowerId <= 135) {
            rarity = 'rare';
          } else if (flowerId >= 136 && flowerId <= 160) {
            rarity = 'super-rare';
          } else if (flowerId >= 161 && flowerId <= 180) {
            rarity = 'epic';
          } else if (flowerId >= 181 && flowerId <= 195) {
            rarity = 'legendary';
          } else if (flowerId >= 196 && flowerId <= 200) {
            rarity = 'mythical';
          }

          ORIGINAL_FLOWER_RARITIES.set(flowerId, rarity);
        }
      }
    }

    // Clear and rebuild flower rarity map
    FLOWER_RARITY_MAP.clear();

    // First, add all preserved original rarities
    for (const [id, rarity] of ORIGINAL_FLOWER_RARITIES) {
      if (AVAILABLE_FLOWER_IDS.includes(id)) {
        FLOWER_RARITY_MAP.set(id, rarity);
      }
    }

    // Find new flowers (201+) that need rarity assignment
    const newFlowerIds = AVAILABLE_FLOWER_IDS.filter(id => id > 200);

    if (newFlowerIds.length > 0) {
      // Assign rarities to new flowers using distribution percentages
      const newRarityAssignments: RarityTier[] = [];

      for (const [rarity, percentage] of Object.entries(FLOWER_RARITY_DISTRIBUTION_PERCENT) as [RarityTier, number][]) {
        const count = Math.floor(newFlowerIds.length * percentage);
        for (let i = 0; i < count; i++) {
          newRarityAssignments.push(rarity);
        }
      }

      // Fill remaining slots with common rarity
      while (newRarityAssignments.length < newFlowerIds.length) {
        newRarityAssignments.push('common');
      }

      // Shuffle assignments for randomness
      const shuffledNewRarities = shuffleArray(newRarityAssignments);

      // Assign rarities to new flower IDs
      newFlowerIds.forEach((flowerId, index) => {
        FLOWER_RARITY_MAP.set(flowerId, shuffledNewRarities[index] || 'common');
      });

      console.log(`üå∏ Assigned rarities to ${newFlowerIds.length} new flowers (201+)`);
    }

    // Debug logging f√ºr Flower Rarity-Verteilung
  const flowerRarityCounts: Record<string, number> = {};
  FLOWER_RARITY_MAP.forEach(rarity => {
    flowerRarityCounts[rarity] = (flowerRarityCounts[rarity] || 0) + 1;
  });

  console.log(`üå∏ Initialized flower system with ${TOTAL_FLOWERS} flowers`);
  console.log(`üå∏ Flower rarity distribution:`, flowerRarityCounts);
  } catch (error) {
    console.error('üå∏ Error initializing flower rarities:', error);

    // Fallback: assign common rarity to all available flowers
    for (const flowerId of AVAILABLE_FLOWER_IDS) {
      FLOWER_RARITY_MAP.set(flowerId, 'common');
    }
  }
}

export async function initializeCreatureSystems(): Promise<void> {
  await Promise.all([
    initializeFishRarities(),
    initializeCaterpillarRarities(),
    initializeFlowerRarities()
  ]);
  console.log(`üåä Creature systems initialized: ${TOTAL_FISH} fish, ${TOTAL_CATERPILLARS} caterpillars, ${TOTAL_FLOWERS} flowers`);
}

// Get fish rarity for specific fish ID
export function getFishRarity(fishId: number): RarityTier {
  return FISH_RARITY_MAP.get(fishId) || 'common';
}

// Get caterpillar rarity for specific caterpillar ID  
export function getCaterpillarRarity(caterpillarId: number): RarityTier {
  return CATERPILLAR_RARITY_MAP.get(caterpillarId) || 'common';
}

// Export available IDs for external use
export function getAvailableFishIds(): number[] {
  return [...AVAILABLE_FISH_IDS];
}

export function getAvailableCaterpillarIds(): number[] {
  return [...AVAILABLE_CATERPILLAR_IDS];
}

// Generate random flower of specific rarity
export async function generateRandomFlower(rarity: RarityTier): Promise<FlowerData> {
  // Get all flower IDs that match the requested rarity
  const matchingIds = Array.from(FLOWER_RARITY_MAP.entries())
    .filter(([id, assignedRarity]) => assignedRarity === rarity)
    .map(([id]) => id);

  // If no flowers of this rarity, fall back to any available ID
  const availableIds = matchingIds.length > 0 ? matchingIds : AVAILABLE_FLOWER_IDS;
  const flowerId = availableIds[Math.floor(Math.random() * availableIds.length)];

  // Generate consistent Latin name using flowerId as seed
  const { generateLatinFlowerName } = await import('../shared/rarity');
  const name = generateLatinFlowerName(flowerId);

  return {
    id: flowerId,
    name,
    imageUrl: `/Blumen/${getFlowerImageFilename(flowerId)}`
  };
}

// Get flower rarity for specific flower ID
export function getFlowerRarity(flowerId: number): RarityTier {
  return FLOWER_RARITY_MAP.get(flowerId) || 'common';
}

// Export available flower IDs for external use
export function getAvailableFlowerIds(): number[] {
  return [...AVAILABLE_FLOWER_IDS];
}

// FlowerData interface to match other creature interfaces
interface FlowerData {
  id: number;
  name: string;
  imageUrl: string;
}

