client/src/components/CaterpillarDetailM.content:
import React, { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { RarityImage } from "./RarityImage";
import { X, Clock, Coins, Timer, ChevronLeft, ChevronRight } from "lucide-react";
import { getRarityColor, getRarityDisplayName, type RarityTier } from "@shared/rarity";
import { toast } from "sonner";

interface CaterpillarDetailProps {
  id: number;
  caterpillarName: string;
  caterpillarRarity: string;
  caterpillarImageUrl: string;
  placedAt?: string;
  userId: number;
}

interface CaterpillarDetailModalProps {
  isOpen: boolean;
  onClose: () => void;
  caterpillar: CaterpillarDetailProps | null;
  onSold: () => void;
  readOnly?: boolean;
  // Navigation props
  currentIndex?: number;
  totalCount?: number;
  onNext?: () => void;
  onPrevious?: () => void;
}

export const CaterpillarDetailModal: React.FC<CaterpillarDetailModalProps> = ({
  isOpen,
  onClose,
  caterpillar,
  onSold,
  readOnly = false,
  currentIndex,
  totalCount,
  onNext,
  onPrevious
}) => {
  const [timeRemaining, setTimeRemaining] = useState<number>(0);
  const [canSell, setCanSell] = useState<boolean>(false);
  const [isSelling, setIsSelling] = useState<boolean>(false);

  // Mouse wheel navigation
  useEffect(() => {
    if (!isOpen || !onNext || !onPrevious) return;

    const handleWheel = (e: Event) => {
      e.preventDefault();
      const wheelEvent = e as WheelEvent;
      if (wheelEvent.deltaY > 0) {
        // Scroll down = Next
        if (currentIndex !== undefined && totalCount !== undefined && currentIndex < totalCount - 1) {
          onNext();
        }
      } else if (wheelEvent.deltaY < 0) {
        // Scroll up = Previous  
        if (currentIndex !== undefined && currentIndex > 0) {
          onPrevious();
        }
      }
    };

    const dialogElement = document.querySelector('[role="dialog"]');
    if (dialogElement) {
      dialogElement.addEventListener('wheel', handleWheel, { passive: false });
      return () => dialogElement.removeEventListener('wheel', handleWheel);
    }
  }, [isOpen, onNext, onPrevious, currentIndex, totalCount]);

  // Calculate countdown every second (12 hours for caterpillars)
  useEffect(() => {
    if (!caterpillar || readOnly) return;

    const fetchSellStatus = async () => {
      try {
        const response = await fetch(`/api/caterpillars/${caterpillar.id}/sell-status`, {
          headers: { 
            'X-User-Id': caterpillar.userId.toString()
          }
        });

        if (response.ok) {
          const data = await response.json();
          setCanSell(data.canSell);
          setTimeRemaining(data.timeRemainingMs);
        } else {
          // Fallback to local calculation (12 hours for caterpillars)
          if (caterpillar.placedAt) {
            const placedTime = new Date(caterpillar.placedAt).getTime();
            const now = new Date().getTime();
            const timeSincePlacement = now - placedTime;
            const TWELVE_HOURS = 12 * 60 * 60 * 1000; // 12h vs 72h for butterflies, 24h for fish
            const remaining = TWELVE_HOURS - timeSincePlacement;

            if (remaining <= 0) {
              setCanSell(true);
              setTimeRemaining(0);
            } else {
              setCanSell(false);
              setTimeRemaining(remaining);
            }
          } else {
            // No placement time means it's a regular caterpillar from garden, immediately sellable
            setCanSell(true);
            setTimeRemaining(0);
          }
        }
      } catch (error) {
        console.error('Failed to fetch caterpillar sell status:', error);
        // For regular caterpillars (not from pond), they're immediately sellable
        if (!caterpillar.placedAt) {
          setCanSell(true);
          setTimeRemaining(0);
        }
      }
    };

    fetchSellStatus();
    const interval = setInterval(fetchSellStatus, 1000);

    return () => clearInterval(interval);
  }, [caterpillar, readOnly]);

  const formatTime = (ms: number): string => {
    if (ms <= 0) return "Verkauf m√∂glich!";

    const totalSeconds = Math.floor(ms / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  const getCaterpillarPrice = (rarity: string): number => {
    // Raupenpreise = 40% der Fischpreise
    const fishPrice = (() => {
      switch (rarity.toLowerCase()) {
        case 'common': return 80;
        case 'uncommon': return 200;
        case 'rare': return 450;
        case 'super-rare': return 940;
        case 'epic': return 1500;
        case 'legendary': return 2200;
        case 'mythical': return 4000;
        default: return 80;
      }
    })();
    return Math.floor(fishPrice * 0.4); // 40% of fish price
  };

  const handleSell = async () => {
    if (!caterpillar || !canSell || isSelling) return;

    setIsSelling(true);
    try {
      const response = await fetch('/api/caterpillars/sell', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-User-Id': caterpillar.userId.toString()
        },
        body: JSON.stringify({
          caterpillarId: caterpillar.id
        })
      });

      if (response.ok) {
        const result = await response.json();
        toast.success(`Raupe verkauft!`, {
          description: `Du hast ${result.creditsEarned} Credits erhalten.`
        });
        onSold();
        onClose();
      } else {
        const errorData = await response.json();
        toast.error('Verkauf fehlgeschlagen', {
          description: errorData.message || 'Die Raupe konnte nicht verkauft werden.'
        });
      }
    } catch (error) {
      console.error('Failed to sell caterpillar:', error);
      toast.error('Verkauf fehlgeschlagen', {
        description: 'Ein Netzwerkfehler ist aufgetreten.'
      });
    } finally {
      setIsSelling(false);
    }
  };

  if (!caterpillar) return null;

  const price = getCaterpillarPrice(caterpillar.caterpillarRarity);
  const rarityColor = getRarityColor(caterpillar.caterpillarRarity as RarityTier);
  const rarityDisplayName = getRarityDisplayName(caterpillar.caterpillarRarity as RarityTier);

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md w-full max-w-[95vw] max-h-[90vh] overflow-y-auto bg-slate-900 text-slate-50 border-slate-700">
        <div className="flex justify-between items-center">
          <DialogHeader className="flex-1">
            <DialogTitle className="text-xl font-bold text-slate-50 pr-8">
              Raupe Details
            </DialogTitle>
          </DialogHeader>

          <div className="flex items-center space-x-2">
            {/* Navigation arrows */}
            {onPrevious && currentIndex !== undefined && currentIndex > 0 && (
              <Button
                variant="ghost"
                size="icon"
                onClick={onPrevious}
                className="h-8 w-8 text-slate-400 hover:text-slate-200 hover:bg-slate-800"
              >
                <ChevronLeft className="h-4 w-4" />
              </Button>
            )}

            {/* Counter */}
            {currentIndex !== undefined && totalCount !== undefined && (
              <div className="text-sm text-slate-400 px-2 min-w-[4rem] text-center">
                {currentIndex + 1}/{totalCount}
              </div>
            )}

            {onNext && currentIndex !== undefined && totalCount !== undefined && currentIndex < totalCount - 1 && (
              <Button
                variant="ghost"
                size="icon"
                onClick={onNext}
                className="h-8 w-8 text-slate-400 hover:text-slate-200 hover:bg-slate-800"
              >
                <ChevronRight className="h-4 w-4" />
              </Button>
            )}

            <Button
              variant="ghost"
              size="icon"
              onClick={onClose}
              className="h-8 w-8 text-slate-400 hover:text-slate-200 hover:bg-slate-800"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </div>

        <div className="space-y-6">
          {/* Caterpillar Image and Basic Info */}
          <Card className="bg-slate-800 border-slate-700">
            <CardContent className="p-6">
              <div className="flex flex-col items-center space-y-4">
                <RarityImage
                  src={caterpillar.caterpillarImageUrl}
                  alt={caterpillar.caterpillarName}
                  rarity={caterpillar.caterpillarRarity as RarityTier}
                  size="xl"
                  className="w-32 h-32"
                />

                <div className="text-center space-y-2">
                  <h3 className="text-2xl font-bold text-slate-50">
                    {caterpillar.caterpillarName}
                  </h3>
                  <Badge 
                    variant="outline" 
                    className="text-sm font-medium border-2"
                    style={{ 
                      borderColor: rarityColor,
                      color: rarityColor,
                      backgroundColor: `${rarityColor}20`
                    }}
                  >
                    {rarityDisplayName}
                  </Badge>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Price Information */}
          <Card className="bg-slate-800 border-slate-700">
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <Coins className="h-5 w-5 text-yellow-400" />
                  <span className="text-slate-300">Verkaufspreis:</span>
                </div>
                <div className="text-2xl font-bold text-yellow-400">
                  {price.toLocaleString()} Credits
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Selling Controls */}
          {!readOnly && (
            <div className="space-y-4">
              {!canSell && (
                <Card className="bg-slate-800 border-slate-700">
                  <CardContent className="p-4">
                    <div className="flex items-center space-x-3">
                      <Clock className="h-5 w-5 text-orange-400" />
                      <div className="flex-1">
                        <p className="text-slate-300 font-medium">Verkauf verf√ºgbar in:</p>
                        <div className="flex items-center space-x-2 mt-1">
                          <Timer className="h-4 w-4 text-orange-400" />
                          <span className="text-lg font-mono text-orange-400">
                            {formatTime(timeRemaining)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}

              <Button
                onClick={handleSell}
                disabled={!canSell || isSelling}
                className={`w-full h-12 text-lg font-semibold transition-all ${
                  canSell 
                    ? 'bg-green-600 hover:bg-green-500 text-white' 
                    : 'bg-slate-700 text-slate-400 cursor-not-allowed'
                }`}
              >
                {isSelling ? (
                  <div className="flex items-center space-x-2">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                    <span>Verkaufe...</span>
                  </div>
                ) : canSell ? (
                  `F√ºr ${price.toLocaleString()} Credits verkaufen`
                ) : (
                  'Noch nicht verkaufbar'
                )}
              </Button>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
};

client/src/components/Top100Modal.tsx.content:
import React, { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Trophy, 
  Coins, 
  TrendingUp, 
  Sun, 
  Heart, 
  Flower, 
  Gift, 
  Bug, 
  Fish, 
  Sparkles,
  Star,
  Crown,
  Users,
  Dna,
  Zap,
  ThumbsUp
} from "lucide-react";
import { useAuth } from "@/lib/stores/useAuth";

type Top100Category = 
  | 'credits' 
  | 'passive-income' 
  | 'suns' 
  | 'likes' 
  | 'dna' 
  | 'seeds' 
  | 'flowers' 
  | 'hearts' 
  | 'butterflies' 
  | 'caterpillars' 
  | 'fish'
  | 'exhibition-butterflies'
  | 'exhibition-fish'
  | 'bouquet-recipes';

interface Top100Player {
  id: number;
  username: string;
  value: number;
  rank: number;
  isCurrentUser?: boolean;
}

interface Top100ModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export const Top100Modal: React.FC<Top100ModalProps> = ({ isOpen, onClose }) => {
  const [selectedCategory, setSelectedCategory] = useState<Top100Category>('credits');
  const [players, setPlayers] = useState<Top100Player[]>([]);
  const [loading, setLoading] = useState(false);
  const { user: currentUser } = useAuth();

  const categories = [
    { id: 'credits', name: 'üí∞ Credits', icon: Coins, color: 'from-yellow-400 to-yellow-600' },
    { id: 'passive-income', name: 'üìà Passives Einkommen', icon: TrendingUp, color: 'from-green-400 to-green-600' },
    { id: 'suns', name: '‚òÄÔ∏è Sonnen', icon: Sun, color: 'from-orange-400 to-orange-600' },
    { id: 'likes', name: 'üëç Likes', icon: ThumbsUp, color: 'from-pink-400 to-pink-600' },
    { id: 'dna', name: 'üß¨ DNA', icon: Dna, color: 'from-violet-400 to-purple-600' },
    { id: 'seeds', name: 'üå± Samen', icon: Sparkles, color: 'from-emerald-400 to-emerald-600' },
    { id: 'flowers', name: 'üå∏ Blumen', icon: Flower, color: 'from-rose-400 to-rose-600' },
    { id: 'hearts', name: 'üíñ Schlosspark-Herzen', icon: Heart, color: 'from-yellow-400 to-amber-600' },
    { id: 'butterflies', name: 'ü¶ã Schmetterlinge', icon: Bug, color: 'from-blue-400 to-blue-600' },
    { id: 'caterpillars', name: 'üêõ Raupen', icon: Bug, color: 'from-lime-400 to-lime-600' },
    { id: 'fish', name: 'üêü Fische', icon: Fish, color: 'from-cyan-400 to-cyan-600' },
    { id: 'exhibition-butterflies', name: 'üèõÔ∏è Ausgestellte Schmetterlinge', icon: Star, color: 'from-indigo-400 to-indigo-600' },
    { id: 'exhibition-fish', name: 'üèõÔ∏è Ausgestellte Fische', icon: Crown, color: 'from-teal-400 to-teal-600' },
    { id: 'bouquet-recipes', name: 'üìú Bouquet Rezepte', icon: Users, color: 'from-slate-400 to-slate-600' }
  ] as const;

  useEffect(() => {
    if (isOpen) {
      fetchTop100Data();
    }
  }, [isOpen, selectedCategory]);

  const fetchTop100Data = async () => {
    if (!currentUser) return;

    setLoading(true);
    console.log(`üèÜ Fetching Top 100 data for category: ${selectedCategory}`);
    try {
      // Add cache-busting timestamp to ensure fresh data
      const timestamp = Date.now();
      const response = await fetch(`/api/rankings/top100/${selectedCategory}?t=${timestamp}`, {
        headers: {
          'X-User-Id': currentUser.id.toString()
        }
      });
      if (response.ok) {
        const data = await response.json();
        console.log(`üèÜ Received Top 100 data:`, data);
        setPlayers(data.players || []);
      } else {
        console.error(`üèÜ Failed to fetch rankings: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      console.error('üèÜ Failed to fetch top 100 data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getCurrentCategory = () => {
    return categories.find(cat => cat.id === selectedCategory);
  };

  const formatValue = (value: number, category: Top100Category) => {
    if (category === 'credits' || category === 'passive-income') {
      return `${value.toLocaleString()} Cr`;
    }
    return value.toLocaleString();
  };

  const getRankIcon = (rank: number) => {
    if (rank === 1) return "ü•á";
    if (rank === 2) return "ü•à";
    if (rank === 3) return "ü•â";
    return `#${rank}`;
  };

  const getRankStyle = (rank: number, isCurrentUser?: boolean) => {
    let baseStyle = "transition-all duration-300 hover:scale-105 border-2 ";

    if (isCurrentUser) {
      baseStyle += "border-cyan-400 bg-gradient-to-r from-cyan-800/50 to-blue-800/50 shadow-cyan-400/30";
    } else if (rank === 1) {
      baseStyle += "border-yellow-400 bg-gradient-to-r from-yellow-800/30 to-yellow-700/30";
    } else if (rank === 2) {
      baseStyle += "border-slate-300 bg-gradient-to-r from-slate-800/30 to-slate-700/30";
    } else if (rank === 3) {
      baseStyle += "border-orange-400 bg-gradient-to-r from-orange-800/30 to-orange-700/30";
    } else {
      baseStyle += "border-slate-600 bg-gradient-to-r from-slate-800 to-slate-900";
    }

    return baseStyle;
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="bg-gradient-to-br from-slate-800 to-slate-900 border-2 border-cyan-500/30 text-white w-[95vw] max-w-sm md:max-w-6xl max-h-[95vh] overflow-hidden shadow-2xl">
        <DialogHeader className="relative pb-6">
          <div className="absolute inset-0 bg-gradient-to-r from-yellow-500/10 to-orange-500/10 rounded-t-lg -mx-6 -my-2"></div>
          <DialogTitle className="flex items-center text-white relative z-10">
            <div className="relative">
              <Trophy className="h-10 w-10 mr-4 text-yellow-400 animate-pulse" />
              <div className="absolute inset-0 h-10 w-10 mr-4 text-yellow-400 animate-ping opacity-30"></div>
            </div>
            <span className="text-4xl font-bold bg-gradient-to-r from-yellow-300 to-orange-300 bg-clip-text text-transparent">
              TOP 100 RANGLISTE
            </span>
          </DialogTitle>
          <DialogDescription className="sr-only">
            Rangliste der besten Spieler mit verschiedenen Kategorien und Statistiken.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 overflow-y-auto max-h-[calc(95vh-200px)]">
          {/* Category Selection */}
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 lg:grid-cols-6 xl:grid-cols-7 gap-2 md:gap-3">
            {categories.map((category) => {
              const Icon = category.icon;
              const isSelected = selectedCategory === category.id;

              return (
                <button
                  key={category.id}
                  onClick={() => setSelectedCategory(category.id as Top100Category)}
                  className={`relative group p-4 rounded-xl border-2 transition-all duration-300 hover:scale-105 ${
                    isSelected 
                      ? `border-cyan-400 bg-gradient-to-br ${category.color} shadow-lg` 
                      : 'border-slate-600 bg-gradient-to-br from-slate-700 to-slate-800 hover:border-slate-400'
                  }`}
                >
                  <div className="flex flex-col items-center space-y-2">
                    <Icon 
                      className={`h-6 w-6 ${isSelected ? 'text-white' : 'text-slate-300'} transition-colors`} 
                    />
                    <span className={`text-xs font-medium text-center leading-tight ${
                      isSelected ? 'text-white' : 'text-slate-300'
                    }`}>
                      {category.name.replace(/^[^\s]+\s/, '')} {/* Remove emoji for better fit */}
                    </span>
                  </div>
                  {isSelected && (
                    <div className="absolute inset-0 bg-gradient-to-br from-white/20 to-white/5 rounded-xl animate-pulse"></div>
                  )}
                </button>
              );
            })}
          </div>

          {/* Current Category Display */}
          <div className="text-center">
            <h2 className="text-3xl font-bold bg-gradient-to-r from-cyan-300 to-blue-300 bg-clip-text text-transparent">
              {getCurrentCategory()?.name}
            </h2>
          </div>

          {/* Players List */}
          {loading ? (
            <div className="text-center py-12 relative">
              <div className="absolute inset-0 bg-gradient-to-r from-yellow-500/5 to-orange-500/5 rounded-lg"></div>
              <div className="relative z-10">
                <div className="relative mb-6">
                  <Trophy className="h-16 w-16 text-yellow-400 mx-auto animate-spin" />
                  <div className="absolute inset-0 h-16 w-16 mx-auto text-yellow-400 animate-ping opacity-20"></div>
                </div>
                <p className="text-slate-300 text-xl">üèÜ Lade Rangliste...</p>
              </div>
            </div>
          ) : players.length === 0 ? (
            <div className="text-center py-12 relative">
              <div className="absolute inset-0 bg-gradient-to-r from-yellow-500/5 to-orange-500/5 rounded-lg"></div>
              <div className="relative z-10">
                <div className="relative mb-6">
                  <Trophy className="h-16 w-16 text-yellow-400 mx-auto animate-bounce" />
                  <div className="absolute inset-0 h-16 w-16 mx-auto text-yellow-400 animate-ping opacity-20"></div>
                </div>
                <p className="text-slate-300 text-xl mb-3">üèÜ Keine Daten verf√ºgbar</p>
                <p className="text-slate-400 text-lg">Die Rangliste konnte nicht geladen werden</p>
              </div>
            </div>
          ) : (
            <div className="space-y-2">
              {players.map((player) => (
                <Card 
                  key={player.id} 
                  className={getRankStyle(player.rank, player.isCurrentUser)}
                >
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        {/* Rank */}
                        <div className="flex items-center justify-center w-12 h-12 rounded-full bg-slate-700/50 border-2 border-slate-600">
                          <span className="text-lg font-bold text-white">
                            {getRankIcon(player.rank)}
                          </span>
                        </div>

                        {/* Player Info */}
                        <div>
                          <h3 className="font-bold text-white text-lg flex items-center">
                            {player.username}
                            {player.isCurrentUser && (
                              <Badge className="ml-2 bg-cyan-500/20 text-cyan-400 border-cyan-400">
                                Das bist du!
                              </Badge>
                            )}
                          </h3>
                        </div>
                      </div>

                      {/* Value */}
                      <div className="text-right">
                        <div className="text-2xl font-bold text-yellow-400">
                          {formatValue(player.value, selectedCategory)}
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex justify-center pt-6">
            <Button
              onClick={onClose}
              className="bg-gradient-to-r from-cyan-500 to-blue-500 hover:from-cyan-600 hover:to-blue-600 text-white text-lg px-8 py-3 font-bold transition-all duration-300 hover:scale-110 shadow-lg"
            >
              ‚úÖ Schlie√üen
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

