import jwt from 'jsonwebtoken';
import { Request, Response, NextFunction } from 'express';

// JWT Secret key - In production, use environment variable
const JWT_SECRET = process.env.JWT_SECRET || 'mariposa-secret-key-2025';
const JWT_EXPIRES_IN = '7d';

export interface JWTPayload {
  userId: number;
  username: string;
}

export interface AuthenticatedRequest extends Request {
  user?: JWTPayload;
}

/**
 * Generate JWT token for user
 */
export function generateToken(user: { id: number; username: string }): string {
  const payload: JWTPayload = {
    userId: user.id,
    username: user.username
  };

  return jwt.sign(payload, JWT_SECRET, { 
    expiresIn: JWT_EXPIRES_IN,
    issuer: 'mariposa-game'
  });
}

/**
 * Verify JWT token and extract user data
 */
export function verifyToken(token: string): JWTPayload | null {
  try {
    const decoded = jwt.verify(token, JWT_SECRET) as JWTPayload;
    return decoded;
  } catch (error) {
    console.warn('JWT verification failed:', error instanceof Error ? error.message : 'Unknown error');
    return null;
  }
}

/**
 * Authentication middleware - validates JWT token from cookies
 */
export function requireAuth(req: AuthenticatedRequest, res: Response, next: NextFunction) {
  try {
    // Extract JWT token from cookies
    const token = req.cookies?.authToken;

    if (!token) {
      return res.status(401).json({ 
        message: 'Authentication required - no token provided',
        code: 'NO_TOKEN'
      });
    }

    // Verify token
    const userData = verifyToken(token);
    if (!userData) {
      return res.status(401).json({ 
        message: 'Invalid or expired authentication token',
        code: 'INVALID_TOKEN'
      });
    }

    // Attach user data to request
    req.user = userData;
    next();
  } catch (error) {
    console.error('Authentication middleware error:', error);
    return res.status(500).json({ 
      message: 'Authentication system error',
      code: 'AUTH_ERROR'
    });
  }
}

/**
 * User authorization middleware - ensures user can only access their own resources
 * Must be used after requireAuth middleware
 */
export function requireUserAccess(req: AuthenticatedRequest, res: Response, next: NextFunction) {
  try {
    if (!req.user) {
      return res.status(401).json({ 
        message: 'Authentication required',
        code: 'NOT_AUTHENTICATED'
      });
    }

    // Extract user ID from URL parameters or headers
    let requestedUserId: number;

    // Check URL parameter first (e.g., /api/user/:id/credits or /api/user/:userId/pond-field)
    if (req.params.id) {
      requestedUserId = parseInt(req.params.id);
    } else if (req.params.userId) {
      requestedUserId = parseInt(req.params.userId);
    } 
    // Fallback to x-user-id header for compatibility
    else if (req.headers['x-user-id']) {
      requestedUserId = parseInt(req.headers['x-user-id'] as string);
    }
    // For endpoints without explicit user ID, extract from request body
    else if (req.body?.userId) {
      requestedUserId = parseInt(req.body.userId);
    }
    else {
      return res.status(400).json({ 
        message: 'User ID not found in request',
        code: 'MISSING_USER_ID'
      });
    }

    // Validate that authenticated user matches the requested user
    if (req.user.userId !== requestedUserId) {
      console.warn(`üö® AUTHORIZATION VIOLATION: User ${req.user.userId} (${req.user.username}) attempted to access resources for user ${requestedUserId}`);
      return res.status(403).json({ 
        message: 'Access denied - you can only access your own resources',
        code: 'ACCESS_DENIED'
      });
    }

    // Add the validated user ID to the request for easy access
    req.validatedUserId = requestedUserId;
    next();
  } catch (error) {
    console.error('User authorization middleware error:', error);
    return res.status(500).json({ 
      message: 'Authorization system error',
      code: 'AUTHZ_ERROR'
    });
  }
}

/**
 * Combined middleware for endpoints that need both authentication and user access control
 */
export function requireAuthenticatedUser(req: AuthenticatedRequest, res: Response, next: NextFunction) {
  requireAuth(req, res, (err) => {
    if (err) return next(err);
    requireUserAccess(req, res, next);
  });
}

/**
 * Optional authentication middleware - adds user data if token is valid, but doesn't require it
 */
export function optionalAuth(req: AuthenticatedRequest, res: Response, next: NextFunction) {
  try {
    const token = req.cookies?.authToken;

    if (token) {
      const userData = verifyToken(token);
      if (userData) {
        req.user = userData;
      }
    }

    next();
  } catch (error) {
    console.error('Optional authentication error:', error);
    next(); // Continue without authentication
  }
}

// Extend Express Request interface
declare global {
  namespace Express {
    interface Request {
      user?: JWTPayload;
      validatedUserId?: number;
    }
  }
}

scripts/backfill-challenge-progress.ts.content:
#!/usr/bin/env tsx

import { drizzle } from 'drizzle-orm/neon-http';
import { neon } from '@neondatabase/serverless';
import { challengeDonations, weeklyChallenges, weeklyChallengeProgress, users } from '../shared/schema.js';
import { eq, and, sql } from 'drizzle-orm';

// Create database connection
const connectionString = process.env.DATABASE_URL;
if (!connectionString) {
  throw new Error('DATABASE_URL environment variable is required');
}

const db = drizzle(neon(connectionString));

// Backfill challenge progress from existing donations
async function backfillChallengeProgress() {
  console.log('üå∏ Starting Challenge Progress Backfill...\n');

  try {
    // Get active challenge
    const activeChallenge = await db
      .select()
      .from(weeklyChallenges)
      .where(eq(weeklyChallenges.isActive, true))
      .limit(1);

    if (activeChallenge.length === 0) {
      console.log('‚ùå No active challenge found');
      return;
    }

    const challenge = activeChallenge[0];
    console.log(`üéØ Active Challenge: Week ${challenge.weekNumber} (ID: ${challenge.id})`);
    console.log(`üìù Required Flowers: ${challenge.flowerId1}, ${challenge.flowerId2}, ${challenge.flowerId3}, ${challenge.flowerId4}, ${challenge.flowerId5}, ${challenge.flowerId6}\n`);

    // Get all donations for this challenge
    const donations = await db
      .select({
        userId: challengeDonations.userId,
        username: users.username,
        flowerId: challengeDonations.flowerId,
        quantity: challengeDonations.quantity,
        donatedAt: challengeDonations.donatedAt
      })
      .from(challengeDonations)
      .leftJoin(users, eq(challengeDonations.userId, users.id))
      .where(eq(challengeDonations.challengeId, challenge.id))
      .orderBy(challengeDonations.userId, challengeDonations.donatedAt);

    console.log(`üìä Found ${donations.length} donations for challenge ${challenge.id}:\n`);

    // Required flower IDs set
    const requiredFlowerIds = new Set([
      challenge.flowerId1, challenge.flowerId2, challenge.flowerId3,
      challenge.flowerId4, challenge.flowerId5, challenge.flowerId6
    ]);

    // Calculate progress for each user
    const userProgress = new Map<number, {
      userId: number;
      username: string;
      score: number;
      totalDonations: number;
      qualifyingFlowers: Set<number>;
      firstCompletedAt: Date | null;
    }>();

    for (const donation of donations) {
      if (!userProgress.has(donation.userId)) {
        userProgress.set(donation.userId, {
          userId: donation.userId,
          username: donation.username || 'Unknown',
          score: 0,
          totalDonations: 0,
          qualifyingFlowers: new Set(),
          firstCompletedAt: null
        });
      }

      const progress = userProgress.get(donation.userId)!;
      progress.totalDonations += donation.quantity;

      // Check if this flower qualifies
      if (requiredFlowerIds.has(donation.flowerId)) {
        const prevScore = progress.score;
        progress.score += donation.quantity;
        progress.qualifyingFlowers.add(donation.flowerId);

        console.log(`‚úÖ ${progress.username}: +${donation.quantity} qualifying donations (Flower ${donation.flowerId}) -> Score: ${progress.score}`);

        // Check if completed first set (6 unique flowers)
        if (progress.qualifyingFlowers.size === 6 && !progress.firstCompletedAt) {
          progress.firstCompletedAt = donation.donatedAt;
          console.log(`üéâ ${progress.username}: COMPLETED first set at ${donation.donatedAt}`);
        }
      } else {
        console.log(`‚ùå ${progress.username}: Non-qualifying donation (Flower ${donation.flowerId})`);
      }
    }

    console.log('\nüìà Final Progress Summary:');
    for (const [userId, progress] of userProgress) {
      console.log(`${progress.username} (ID: ${userId}): Score ${progress.score}, Total Donations: ${progress.totalDonations}, Qualifying Flowers: ${Array.from(progress.qualifyingFlowers).join(', ')}, First Completed: ${progress.firstCompletedAt || 'Not completed'}`);
    }

    // Insert/Update progress in database
    console.log('\nüíæ Updating database...');
    for (const [userId, progress] of userProgress) {
      await db
        .insert(weeklyChallengeProgress)
        .values({
          userId: progress.userId,
          challengeId: challenge.id,
          score: progress.score,
          setsCompleted: progress.qualifyingFlowers.size === 6 ? 1 : 0,
          totalDonations: progress.totalDonations,
          firstCompletedAt: progress.firstCompletedAt,
          lastUpdatedAt: new Date(),
          createdAt: new Date()
        })
        .onConflictDoUpdate({
          target: [weeklyChallengeProgress.userId, weeklyChallengeProgress.challengeId],
          set: {
            score: progress.score,
            setsCompleted: progress.qualifyingFlowers.size === 6 ? 1 : 0,
            totalDonations: progress.totalDonations,
            firstCompletedAt: progress.firstCompletedAt,
            lastUpdatedAt: new Date()
          }
        });

      console.log(`‚úÖ Updated progress for ${progress.username}`);
    }

    // Check final leaderboard
    console.log('\nüèÜ Final Leaderboard:');
    const leaderboard = await db
      .select({
        userId: weeklyChallengeProgress.userId,
        score: weeklyChallengeProgress.score,
        firstCompletedAt: weeklyChallengeProgress.firstCompletedAt,
        username: users.username
      })
      .from(weeklyChallengeProgress)
      .leftJoin(users, eq(weeklyChallengeProgress.userId, users.id))
      .where(eq(weeklyChallengeProgress.challengeId, challenge.id))
      .orderBy(sql`score DESC, first_completed_at ASC`);

    if (leaderboard.length === 0) {
      console.log('‚ùå No qualifying entries found - leaderboard is empty');
    } else {
      leaderboard.forEach((entry, index) => {
        console.log(`${index + 1}. ${entry.username}: ${entry.score} points ${entry.firstCompletedAt ? `(completed ${entry.firstCompletedAt})` : '(incomplete)'}`);
      });
    }

    console.log('\nüéâ Backfill completed successfully!');

  } catch (error) {
    console.error('‚ùå Error during backfill:', error);
    throw error;
  }
}

// Run the backfill
backfillChallengeProgress().catch(console.error);

client/src/components/LoginNotifications.content:
import { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './ui/dialog';
import { Button } from './ui/button';
import { Bell, X, Trophy, Gift, Star, Award } from 'lucide-react';
import { Badge } from './ui/badge';
import { ScrollArea } from './ui/scroll-area';

interface UserNotification {
  id: number;
  userId: number;
  type: string;
  title: string;
  message: string;
  isRead: boolean;
  rewardType?: string;
  rewardItemId?: number;
  rewardItemName?: string;
  rewardItemRarity?: string;
  rewardAmount?: number;
  challengeId?: number;
  challengeRank?: number;
  createdAt: string;
  readAt?: string;
}

interface LoginNotificationsProps {
  userId: number;
  isOpen: boolean;
  onClose: () => void;
}

const getNotificationIcon = (type: string, rewardType?: string) => {
  if (type === 'challenge_reward') {
    if (rewardType === 'vip_butterfly') return <Star className="w-6 h-6 text-yellow-500" />;
    if (rewardType === 'butterfly') return <Trophy className="w-6 h-6 text-purple-500" />;
    if (rewardType === 'credits') return <Gift className="w-6 h-6 text-green-500" />;
  }
  return <Bell className="w-6 h-6 text-blue-500" />;
};

const getRarityColor = (rarity?: string) => {
  switch (rarity?.toLowerCase()) {
    case 'common': return 'bg-yellow-500';
    case 'uncommon': return 'bg-green-500';
    case 'rare': return 'bg-blue-500';
    case 'super-rare': return 'bg-cyan-500';
    case 'epic': return 'bg-purple-500';
    case 'legendary': return 'bg-orange-500';
    case 'mythical': return 'bg-red-500';
    case 'vip': return 'bg-gradient-to-r from-yellow-400 to-orange-500';
    default: return 'bg-gray-500';
  }
};

export function LoginNotifications({ userId, isOpen, onClose }: LoginNotificationsProps) {
  const [notifications, setNotifications] = useState<UserNotification[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (isOpen && userId) {
      loadNotifications();
    }
  }, [isOpen, userId]);

  const loadNotifications = async () => {
    setIsLoading(true);
    try {
      const response = await fetch(`/api/user/${userId}/notifications`);
      if (response.ok) {
        const data = await response.json();
        setNotifications(data.notifications || []);
      }
    } catch (error) {
      console.error('Failed to load notifications:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const markAsRead = async (notificationId: number) => {
    try {
      const response = await fetch(`/api/user/${userId}/notifications/${notificationId}/read`, {
        method: 'POST'
      });
      if (response.ok) {
        setNotifications(prev => 
          prev.map(n => n.id === notificationId ? { ...n, isRead: true } : n)
        );
      }
    } catch (error) {
      console.error('Failed to mark notification as read:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      const response = await fetch(`/api/user/${userId}/notifications/read-all`, {
        method: 'POST'
      });
      if (response.ok) {
        setNotifications(prev => prev.map(n => ({ ...n, isRead: true })));
      }
    } catch (error) {
      console.error('Failed to mark all notifications as read:', error);
    }
  };

  const handleClose = () => {
    // Auto-mark all as read when closing
    if (notifications.some(n => !n.isRead)) {
      markAllAsRead();
    }
    onClose();
  };

  if (notifications.length === 0 && !isLoading) {
    return null; // Don't show modal if no notifications
  }

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md bg-gradient-to-br from-purple-50 to-pink-50 border-2 border-purple-200">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-purple-800">
            <Bell className="w-5 h-5" />
            Neue Nachrichten
            {notifications.length > 0 && (
              <Badge variant="secondary" className="bg-purple-100 text-purple-800">
                {notifications.length}
              </Badge>
            )}
          </DialogTitle>
        </DialogHeader>

        {isLoading ? (
          <div className="flex items-center justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-600"></div>
          </div>
        ) : (
          <ScrollArea className="max-h-96 pr-4">
            <div className="space-y-4">
              {notifications.map((notification) => (
                <div
                  key={notification.id}
                  className="p-4 bg-white rounded-lg border-l-4 border-purple-400 shadow-sm hover:shadow-md transition-shadow"
                >
                  <div className="flex items-start gap-3">
                    <div className="flex-shrink-0 mt-1">
                      {getNotificationIcon(notification.type, notification.rewardType)}
                    </div>

                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2 mb-1">
                        <h4 className="font-semibold text-gray-900 text-sm">
                          {notification.title}
                        </h4>
                        {notification.rewardItemRarity && (
                          <Badge 
                            className={`text-xs text-white ${getRarityColor(notification.rewardItemRarity)}`}
                          >
                            {notification.rewardItemRarity}
                          </Badge>
                        )}
                      </div>

                      <p className="text-sm text-gray-600 mb-2">
                        {notification.message}
                      </p>

                      {notification.challengeRank && (
                        <div className="flex items-center gap-1 text-xs text-purple-600">
                          <Award className="w-3 h-3" />
                          Platz {notification.challengeRank} in der Weekly Challenge
                        </div>
                      )}

                      <div className="text-xs text-gray-400 mt-2">
                        {new Date(notification.createdAt).toLocaleDateString('de-DE', {
                          day: '2-digit',
                          month: '2-digit', 
                          year: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </div>
                    </div>

                    {!notification.isRead && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => markAsRead(notification.id)}
                        className="text-xs text-purple-600 hover:text-purple-800"
                      >
                        Als gelesen markieren
                      </Button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </ScrollArea>
        )}

        <div className="flex justify-between items-center pt-4 border-t">
          {notifications.some(n => !n.isRead) && (
            <Button
              variant="outline"
              size="sm"
              onClick={markAllAsRead}
              className="text-purple-600 border-purple-300 hover:bg-purple-50"
            >
              Alle als gelesen markieren
            </Button>
          )}

          <Button 
            onClick={handleClose}
            className="bg-purple-600 hover:bg-purple-700 text-white ml-auto"
          >
            Schlie√üen
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}
